:root {
  --bg: #070a12;
  --text: #f0f4fb;
  --panel: #121a2b;
  --border: #263249;
  --accent: #86e8ff;
  --muted: #8a95a8;
  --purple: #ae52ff;
  --pink: #ff40d6;
  --hue-shift: 0deg;
}

* { margin: 0; padding: 0; box-sizing: border-box; }

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: var(--bg);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
}

/* Fixed layers */
#velocity-canvas, #hero-canvas {
  position: fixed;
  inset: 0;
  pointer-events: none;
  z-index: 0;
}

#velocity-canvas { z-index: 2; }
#hero-canvas { z-index: 1; }

section { position: relative; z-index: 3; }

/* ── Hero ── */
.hero {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5rem 2rem;
  background: radial-gradient(ellipse at 50% 40%, rgba(134, 232, 255, 0.04) 0%, transparent 65%);
}

.hero-content {
  max-width: 680px;
  text-align: center;
}

.hero-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  border: 1px solid rgba(134, 232, 255, 0.3);
  border-radius: 20px;
  padding: 0.3rem 1rem;
  margin-bottom: 1.5rem;
}

.hero h1 {
  font-size: 4rem;
  font-weight: 900;
  line-height: 1.05;
  margin-bottom: 1.5rem;
  letter-spacing: -2px;
}

.hero-desc {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2.5rem;
  line-height: 1.8;
}

/* Speed indicator */
.speed-indicator {
  display: flex;
  align-items: center;
  gap: 0.8rem;
  max-width: 320px;
  margin: 0 auto;
  padding: 0.8rem 1.2rem;
  background: rgba(18, 26, 43, 0.8);
  border: 1px solid var(--border);
  border-radius: 30px;
  backdrop-filter: blur(8px);
}

.speed-label {
  font-size: 0.72rem;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: var(--muted);
  white-space: nowrap;
}

.speed-bar-track {
  flex: 1;
  height: 4px;
  background: var(--border);
  border-radius: 2px;
  overflow: hidden;
}

.speed-bar-fill {
  height: 100%;
  width: 0%;
  background: linear-gradient(to right, var(--accent), var(--purple));
  border-radius: 2px;
  transition: width 0.1s linear;
}

.speed-value {
  font-size: 0.8rem;
  font-weight: 700;
  color: var(--accent);
  min-width: 28px;
  text-align: right;
  font-variant-numeric: tabular-nums;
}

/* ── Gradient Text ── */
.gradient-text {
  background: linear-gradient(
    90deg,
    hsl(var(--hue-shift), 80%, 55%),
    hsl(calc(var(--hue-shift) + 70deg), 80%, 55%),
    hsl(calc(var(--hue-shift) + 140deg), 80%, 55%)
  );
  background-size: 200% 100%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradShift 6s linear infinite;
}

@keyframes gradShift {
  0% { background-position: 0% center; }
  100% { background-position: 200% center; }
}

@supports not (-webkit-background-clip: text) {
  .gradient-text { color: var(--accent); background: none; }
}

/* ── Depth Section ── */
.depth-section {
  padding: 8rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(7, 10, 18, 0.85);
  backdrop-filter: blur(4px);
}

.depth-cards {
  max-width: 1000px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
  perspective: 1200px;
}

.depth-card {
  padding: 2rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 12px;
  transition: border-color 0.3s ease, box-shadow 0.3s ease;
}

.depth-card:hover {
  border-color: hsl(var(--card-hue), 80%, 55%);
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.4);
}

.dc-icon {
  font-size: 2rem;
  color: hsl(var(--card-hue), 80%, 55%);
  margin-bottom: 1rem;
}

.depth-card h3 {
  font-size: 1.1rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.8rem;
}

.depth-card p {
  font-size: 0.9rem;
  color: var(--muted);
  line-height: 1.7;
}

/* ── Stats ── */
.stats-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(7, 10, 18, 0.9);
}

.section-header {
  text-align: center;
  max-width: 560px;
  margin: 0 auto 4rem;
}

.section-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

.section-header h2 {
  font-size: 2.5rem;
  font-weight: 800;
  letter-spacing: -0.5px;
}

.stats-grid {
  max-width: 800px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1.5rem;
}

.stat-card {
  text-align: center;
  padding: 2rem 1rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 10px;
  transition: all 0.3s ease;
}

.stat-card:hover {
  border-color: var(--accent);
  transform: translateY(-3px);
}

.stat-value {
  font-size: 2rem;
  font-weight: 800;
  color: var(--accent);
  letter-spacing: -1px;
  margin-bottom: 0.5rem;
  font-variant-numeric: tabular-nums;
}

.stat-label {
  font-size: 0.8rem;
  color: var(--muted);
  line-height: 1.5;
}

/* ── Features ── */
.features-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(7, 10, 18, 0.85);
}

.features-list {
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 0;
}

.feature-item {
  display: flex;
  gap: 2rem;
  align-items: flex-start;
  padding: 2rem 0;
  border-bottom: 1px solid var(--border);
}

.feature-item:last-child { border-bottom: none; }

.feature-num {
  font-size: 0.7rem;
  font-weight: 800;
  letter-spacing: 2px;
  color: var(--accent);
  opacity: 0.5;
  padding-top: 0.3rem;
  flex-shrink: 0;
  width: 28px;
}

.feature-text h3 {
  font-size: 1.1rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.6rem;
}

.feature-text p {
  font-size: 0.95rem;
  color: var(--muted);
  line-height: 1.8;
}

code {
  background: rgba(134, 232, 255, 0.1);
  color: var(--accent);
  padding: 0.15rem 0.4rem;
  border-radius: 4px;
  font-family: 'Monaco', monospace;
  font-size: 0.88em;
}

/* ── CTA ── */
.cta-section {
  padding: 8rem 2rem;
  text-align: center;
  border-top: 1px solid var(--border);
  background: radial-gradient(ellipse at 50% 50%, rgba(134, 232, 255, 0.06) 0%, transparent 70%);
}

.cta-section h2 {
  font-size: 3.5rem;
  font-weight: 900;
  margin-bottom: 1rem;
  letter-spacing: -1.5px;
}

.cta-section p {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2rem;
}

.btn-primary {
  display: inline-block;
  padding: 0.9rem 2rem;
  background: var(--accent);
  color: var(--bg);
  border-radius: 6px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-primary:hover {
  background: rgba(134, 232, 255, 0.85);
  transform: translateY(-2px);
}

/* ── Responsive ── */
@media (max-width: 900px) {
  .stats-grid { grid-template-columns: repeat(2, 1fr); }
}

@media (max-width: 768px) {
  .hero h1 { font-size: 2.8rem; }
  .depth-cards { grid-template-columns: 1fr; }
  .stats-grid { grid-template-columns: repeat(2, 1fr); }
  .section-header h2 { font-size: 2rem; }
}

@media (max-width: 480px) {
  .hero h1 { font-size: 2.2rem; }
  .cta-section h2 { font-size: 2.5rem; }
  .stats-grid { grid-template-columns: 1fr 1fr; }
}

html.reduced-motion #velocity-canvas { display: none; }
html.reduced-motion .gradient-text { animation: none !important; }
html.reduced-motion * {
  animation-duration: 0.01ms !important;
  transition-duration: 0.01ms !important;
}

