*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

:root {
  --accent: #86e8ff;
  --neon-purple: #ae52ff;
  --neon-pink: #ff40d6;
}

body {
  background: #030510;
  color: #f0f4fb;
  font-family: 'Inter', 'SF Pro Display', system-ui, sans-serif;
  overflow: hidden;
  height: 100vh;
  cursor: none;
}

@media (hover: none) {
  body { cursor: auto; }
}

/* ── Stage ── */
.stage {
  position: relative;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
}

/* ── Hidden layer (colorful content underneath) ── */
.hidden-layer {
  position: absolute;
  inset: 0;
  z-index: 1;
  background: linear-gradient(135deg, #0a0520 0%, #150a30 50%, #0d1a2a 100%);
}

.hidden-content {
  position: relative;
  width: 100%;
  height: 100%;
}

.color-block {
  position: absolute;
  border-radius: 16px;
}

.block-1 {
  width: 200px; height: 200px;
  background: linear-gradient(135deg, #86e8ff, #3d9eff);
  top: 10%; left: 8%;
  transform: rotate(-12deg);
}

.block-2 {
  width: 150px; height: 260px;
  background: linear-gradient(135deg, #ae52ff, #7c2aff);
  top: 15%; right: 12%;
  transform: rotate(8deg);
}

.block-3 {
  width: 280px; height: 140px;
  background: linear-gradient(135deg, #ff40d6, #ff6b9d);
  bottom: 20%; left: 15%;
  transform: rotate(5deg);
}

.block-4 {
  width: 120px; height: 120px;
  background: linear-gradient(135deg, #ffcc66, #ff9f43);
  top: 55%; left: 50%;
  border-radius: 50%;
}

.block-5 {
  width: 180px; height: 100px;
  background: linear-gradient(135deg, #50c878, #27ae60);
  bottom: 10%; right: 20%;
  transform: rotate(-6deg);
}

.block-6 {
  width: 100px; height: 200px;
  background: linear-gradient(135deg, #ff6b6b, #ee5a24);
  top: 5%; left: 45%;
  transform: rotate(15deg);
}

.orb {
  position: absolute;
  border-radius: 50%;
  filter: blur(40px);
}

.orb-1 {
  width: 300px; height: 300px;
  background: rgba(134, 232, 255, 0.2);
  top: 30%; left: 30%;
}

.orb-2 {
  width: 250px; height: 250px;
  background: rgba(174, 82, 255, 0.15);
  bottom: 15%; right: 25%;
}

.orb-3 {
