/* ─── Reset & Base ───────────────────────────────────────────────────────── */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg:      #0a0a0a;
  --text:    #f5f5f5;
  --muted:   #555;
  --accent:  #e8ff00;
  --border:  #1c1c1c;
  --rule:    1px solid var(--border);
  --pad:     clamp(1.5rem, 5vw, 4rem);
}

html {
  font-size: 16px;
  scroll-behavior: auto; /* Lenis handles smoothing */
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: -apple-system, 'Helvetica Neue', Arial, sans-serif;
  font-weight: 400;
  line-height: 1.5;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* Reduced-motion: disable all transitions */
.reduced-motion *, .reduced-motion *::before, .reduced-motion *::after {
  transition-duration: 0.001ms !important;
  animation-duration: 0.001ms !important;
}

/* ─── Reusable ───────────────────────────────────────────────────────────── */
.label {
  font-family: 'SF Mono', 'Fira Code', 'Courier New', monospace;
  font-size: 0.65rem;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--muted);
}

.label--right { margin-left: auto; }

.section-index {
  font-family: 'SF Mono', 'Fira Code', 'Courier New', monospace;
  font-size: 0.6rem;
  color: var(--accent);
  letter-spacing: 0.1em;
}

.section-label {
  font-family: 'SF Mono', 'Fira Code', 'Courier New', monospace;
  font-size: 0.6rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: var(--muted);
}

/* ─── Hero ───────────────────────────────────────────────────────────────── */
.hero {
  min-height: 100dvh;
  display: flex;
  flex-direction: column;
  border-bottom: var(--rule);
}

.hero__inner {
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: var(--pad);
  padding-top: calc(var(--pad) + 3rem); /* offset for demo-shell bar */
}

.hero__meta {
  display: flex;
  align-items: center;
  margin-bottom: clamp(3rem, 8vh, 6rem);
  border-bottom: var(--rule);
  padding-bottom: 1rem;
}

.hero__name {
  font-size: clamp(4rem, 14vw, 12rem);
  font-weight: 900;
  line-height: 0.92;
  letter-spacing: -0.03em;
  text-transform: uppercase;
  flex: 1;
  /* overflow clip for char animation */
  overflow: hidden;
}

/* SplitText wraps each line; clip the parent */
.hero__name .line { overflow: hidden; }
.hero__name .char { display: inline-block; }

.hero__foot {
