/* ── Demo 52: E-Learning Platform — Meridian ── */
/* Design: Fresh indigo/violet on white — educational, approachable, modern */
/* Fonts: Outfit (geometric sans) + JetBrains Mono (code/data) */

:root {
  --bg: #fafbff;
  --white: #ffffff;
  --surface: #f3f4ff;
  --border: #e4e6f8;
  --text: #111230;
  --muted: #6b6e8a;
  --indigo: #4f46e5;
  --indigo-light: #818cf8;
  --indigo-dim: rgba(79, 70, 229, 0.08);
  --violet: #7c3aed;
  --green: #059669;
  --amber: #d97706;
  --red: #dc2626;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

html { scroll-behavior: smooth; }

body {
  font-family: 'Outfit', -apple-system, sans-serif;
  background: var(--bg);
  color: var(--text);
  -webkit-font-smoothing: antialiased;
  line-height: 1.6;
  overflow-x: hidden;
}

/* ── Section tag ── */
.section-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 0.14em;
  text-transform: uppercase;
  color: var(--indigo);
  margin-bottom: 0.75rem;
}

/* ── Buttons ── */
.btn-primary {
  display: inline-block;
  padding: 0.8rem 1.75rem;
  background: var(--indigo);
  color: white;
  font-family: 'Outfit', sans-serif;
  font-size: 0.88rem;
  font-weight: 600;
  border-radius: 6px;
  text-decoration: none;
  transition: background 0.2s, transform 0.2s, box-shadow 0.2s;
  box-shadow: 0 2px 12px rgba(79, 70, 229, 0.3);
}

.btn-primary:hover {
  background: var(--violet);
  transform: translateY(-2px);
  box-shadow: 0 4px 20px rgba(79, 70, 229, 0.4);
}

.btn-primary--lg {
  padding: 1rem 2.25rem;
  font-size: 1rem;
}

.btn-text {
  font-size: 0.88rem;
  font-weight: 500;
  color: var(--muted);
  text-decoration: none;
  transition: color 0.2s;
}

.btn-text:hover { color: var(--text); }

.btn-ghost {
  font-size: 0.9rem;
  font-weight: 500;
  color: var(--indigo);
  text-decoration: none;
  transition: gap 0.2s;
}

.btn-ghost:hover { text-decoration: underline; }

.btn-outline-dark {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  border: 1px solid var(--border);
  background: transparent;
  color: var(--text);
  font-size: 0.88rem;
  font-weight: 500;
  border-radius: 6px;
  text-decoration: none;
  transition: border-color 0.2s, background 0.2s;
}

.btn-outline-dark:hover {
  border-color: var(--indigo);
  background: var(--indigo-dim);
}

/* ── Nav ── */
.nav {
  position: fixed;
  top: 0; left: 0; right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem 3rem;
  background: rgba(250, 251, 255, 0.9);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid var(--border);
}

.nav-logo {
  display: flex;
  align-items: center;
  gap: 0.6rem;
  text-decoration: none;
  font-weight: 700;
  font-size: 1.05rem;
  color: var(--text);
}

.logo-mark {
  width: 24px;
  height: 24px;
  background: var(--indigo);
  border-radius: 6px;
  position: relative;
}

.logo-mark::after {
  content: '';
  position: absolute;
  top: 4px; left: 4px;
  width: 16px; height: 16px;
  background: rgba(255,255,255,0.3);
  border-radius: 3px;
}

.nav-links {
  display: flex;
  gap: 2rem;
  list-style: none;
}

.nav-links a {
  font-size: 0.88rem;
  font-weight: 500;
  color: var(--muted);
  text-decoration: none;
  transition: color 0.2s;
}

.nav-links a:hover { color: var(--text); }

.nav-ctas {
  display: flex;
  align-items: center;
  gap: 1rem;
}

/* ── Hero ── */
.hero {
  padding: 7rem 3rem 5rem;
  background: var(--bg);
  min-height: 100vh;
  display: flex;
  align-items: center;
}

.hero-inner {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
}

.hero-badge {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.35rem 0.9rem;
  background: var(--surface);
  border: 1px solid var(--border);
  border-radius: 100px;
  font-size: 0.78rem;
  font-weight: 500;
  color: var(--muted);
  margin-bottom: 1.5rem;
}

.hb-dot {
  width: 7px;
  height: 7px;
  background: var(--green);
  border-radius: 50%;
  animation: pulse-green 2s ease-in-out infinite;
}

@keyframes pulse-green {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

.hero-h1 {
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  line-height: 1.15;
  letter-spacing: -0.03em;
  margin-bottom: 1.25rem;
}

.h1-highlight {
  background: linear-gradient(90deg, var(--indigo), var(--violet));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.hero-p {
  font-size: 1.05rem;
  color: var(--muted);
  line-height: 1.8;
  margin-bottom: 2rem;
  max-width: 460px;
}

.hero-actions {
  display: flex;
  gap: 1rem;
  align-items: center;
  margin-bottom: 3rem;
}

.hero-stats {
  display: flex;
  gap: 2rem;
  align-items: center;
  padding-top: 2rem;
  border-top: 1px solid var(--border);
}

.hs-item { display: flex; flex-direction: column; gap: 0.2rem; }

.hs-num {
  font-family: 'JetBrains Mono', monospace;
  font-size: 1.6rem;
  font-weight: 700;
  color: var(--text);
  line-height: 1;
}

.hs-label {
  font-size: 0.72rem;
  color: var(--muted);
  font-weight: 500;
}

.hs-div {
  width: 1px;
  height: 36px;
  background: var(--border);
}

/* ── Progress card (hero visual) ── */
.hero-visual {
  position: relative;
}

.progress-card {
  background: var(--white);
  border: 1px solid var(--border);
  border-radius: 16px;
  padding: 1.5rem;
  box-shadow: 0 8px 40px rgba(79, 70, 229, 0.1);
  position: relative;
  z-index: 2;
}

.pc-header {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 1rem;
}

.pc-avatar {
  width: 36px;
  height: 36px;
  border-radius: 8px;
  background: linear-gradient(135deg, var(--indigo), var(--violet));
  flex-shrink: 0;
}

.pc-meta {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 0.15rem;
}

.pc-meta strong {
  font-size: 0.9rem;
  font-weight: 700;
  color: var(--text);
}

.pc-meta span {
  font-size: 0.72rem;
  color: var(--muted);
}

.pc-pct {
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.8rem;
  font-weight: 700;
  color: var(--indigo);
}

.pc-progress {
  height: 6px;
  background: var(--surface);
  border-radius: 3px;
  overflow: hidden;
  margin-bottom: 1.25rem;
}

.pc-fill {
  height: 100%;
  width: 0%;
  background: linear-gradient(90deg, var(--indigo), var(--indigo-light));
  border-radius: 3px;
  transition: width 0.1s linear;
}

.pc-lessons {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.pc-lesson {
  font-size: 0.82rem;
  color: var(--muted);
  padding: 0.5rem 0.75rem;
  border-radius: 6px;
  transition: background 0.2s;
}

.pc-lesson--done {
  color: var(--green);
  background: rgba(5, 150, 105, 0.06);
}

.pc-lesson--active {
  color: var(--indigo);
  background: var(--indigo-dim);
  font-weight: 600;
}

/* Floating skill badges */
.skill-badge {
  position: absolute;
  padding: 0.4rem 0.9rem;
  background: var(--white);
  border: 1px solid var(--border);
  border-radius: 100px;
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--text);
  box-shadow: 0 4px 16px rgba(0,0,0,0.08);
  white-space: nowrap;
}

.sb--1 { top: -1.5rem; right: 1.5rem; }
.sb--2 { bottom: 1.5rem; right: -1.5rem; }
.sb--3 { bottom: -1.5rem; left: 1rem; }
.sb--4 { top: 2rem; left: -1.5rem; }

/* ── Skills section ── */
.skills-section {
  padding: 6rem 3rem;
  background: var(--text);
  color: white;
}

.skills-inner {
  max-width: 1100px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}

.skills-inner .section-tag { color: var(--indigo-light); }

.skills-header h2 {
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 800;
  line-height: 1.2;
  letter-spacing: -0.02em;
  margin-bottom: 1rem;
}

.skills-header p {
  font-size: 0.95rem;
  color: rgba(255,255,255,0.55);
  line-height: 1.8;
}

.skill-tree {
  position: relative;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: repeat(6, 56px);
  gap: 0;
  min-height: 400px;
}

#tree-canvas {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
}

.st-node {
  position: absolute;
  width: 100px;
  padding: 0.45rem 0.8rem;
  border: 1px solid rgba(255,255,255,0.15);
  border-radius: 6px;
  font-size: 0.72rem;
  font-weight: 600;
  color: rgba(255,255,255,0.5);
  background: rgba(255,255,255,0.05);
  text-align: center;
  transform: translate(calc((var(--col) - 1) * 110px + 10px), calc((var(--row) - 1) * 60px));
  transition: all 0.3s ease;
  cursor: default;
}

.st-node--done {
  border-color: var(--green);
  color: var(--green);
  background: rgba(5, 150, 105, 0.1);
}

.st-node--active {
  border-color: var(--indigo-light);
  color: white;
  background: rgba(99, 102, 241, 0.2);
  box-shadow: 0 0 16px rgba(99, 102, 241, 0.3);
}

.st-node--locked {
  opacity: 0.3;
}

/* ── Courses ── */
.courses-section {
  padding: 6rem 3rem;
  max-width: 1200px;
  margin: 0 auto;
}

.courses-header {
  display: flex;
  align-items: baseline;
  justify-content: space-between;
  flex-wrap: wrap;
  gap: 1rem;
  margin-bottom: 2.5rem;
}

.courses-header h2 {
  font-size: clamp(1.8rem, 3vw, 2.5rem);
  font-weight: 800;
  letter-spacing: -0.02em;
}

.filter-tabs {
  display: flex;
  gap: 0.4rem;
}

.ft-btn {
  padding: 0.4rem 1rem;
  border: 1px solid var(--border);
  background: transparent;
  color: var(--muted);
  font-family: 'Outfit', sans-serif;
  font-size: 0.82rem;
  font-weight: 500;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.2s;
}

.ft-btn.active, .ft-btn:hover {
  background: var(--indigo);
  color: white;
  border-color: var(--indigo);
}

.courses-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.25rem;
}

.course-card {
  background: var(--white);
  border: 1px solid var(--border);
  border-radius: 12px;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  cursor: pointer;
}

.course-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 16px 48px rgba(79, 70, 229, 0.12);
}

.cc-art {
  height: 140px;
}

.cc-art--1 { background: linear-gradient(135deg, #4f46e5, #7c3aed); }
.cc-art--2 { background: linear-gradient(135deg, #0891b2, #0d9488); }
.cc-art--3 { background: linear-gradient(135deg, #d97706, #dc2626); }
.cc-art--4 { background: linear-gradient(135deg, #7c3aed, #db2777); }
.cc-art--5 { background: linear-gradient(135deg, #059669, #0891b2); }
.cc-art--6 { background: linear-gradient(135deg, #1d4ed8, #4f46e5); }

.cc-body {
  padding: 1.25rem;
}

.cc-tags {
  display: flex;
  gap: 0.4rem;
  margin-bottom: 0.75rem;
}

.cc-tag {
  font-size: 0.65rem;
  font-weight: 700;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  padding: 0.2rem 0.55rem;
  border-radius: 4px;
  background: var(--indigo-dim);
  color: var(--indigo);
}

.cc-tag--dev { background: rgba(8, 145, 178, 0.1); color: #0891b2; }
.cc-tag--data { background: rgba(217, 119, 6, 0.1); color: var(--amber); }

.cc-level {
  font-size: 0.65rem;
  font-weight: 500;
  color: var(--muted);
  padding: 0.2rem 0.55rem;
  border: 1px solid var(--border);
  border-radius: 4px;
}

.cc-body h3 {
  font-size: 1rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  line-height: 1.3;
}

.cc-body p {
  font-size: 0.82rem;
  color: var(--muted);
  line-height: 1.65;
  margin-bottom: 1rem;
}

.cc-footer { display: flex; flex-direction: column; gap: 0.5rem; }

.cc-progress-bar {
  height: 4px;
  background: var(--surface);
  border-radius: 2px;
  overflow: hidden;
}

.cc-progress-fill {
  height: 100%;
  width: var(--w, 0%);
  background: var(--indigo);
  border-radius: 2px;
  transform-origin: left;
  transform: scaleX(0);
}

.cc-meta {
  display: flex;
  gap: 0.4rem;
  font-size: 0.72rem;
  color: var(--muted);
}

.cc-dot { color: var(--border); }

/* ── Pricing ── */
.pricing-section {
  padding: 6rem 3rem;
  background: var(--surface);
  border-top: 1px solid var(--border);
}

.pricing-header {
  text-align: center;
  margin-bottom: 3rem;
}

.pricing-header h2 {
