  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 2rem;
  position: relative;
}

.price-card--featured {
  border-color: var(--indigo);
  box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.1);
}

.prce-badge {
  position: absolute;
  top: -12px;
  left: 50%;
  transform: translateX(-50%);
  padding: 0.25rem 0.75rem;
  background: var(--indigo);
  color: white;
  font-size: 0.65rem;
  font-weight: 700;
  letter-spacing: 0.08em;
  border-radius: 100px;
  white-space: nowrap;
}

.prce-tier {
  font-size: 0.8rem;
  font-weight: 700;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  color: var(--muted);
  margin-bottom: 0.75rem;
}

.prce-price {
  font-family: 'JetBrains Mono', monospace;
  font-size: 2.5rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 1.5rem;
  line-height: 1;
}

.prce-price span {
  font-size: 0.85rem;
  color: var(--muted);
  font-weight: 400;
}

.prce-features {
  list-style: none;
  display: flex;
  flex-direction: column;
  gap: 0.6rem;
  margin-bottom: 1.5rem;
}

.prce-features li {
  font-size: 0.85rem;
  color: var(--text);
}

.prce-off { color: var(--muted); text-decoration: line-through; }

/* ── FAQ accordion ── */
.faq-section {
  padding: 6rem 3rem;
  max-width: 700px;
  margin: 0 auto;
}

.faq-section h2 {
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 800;
  letter-spacing: -0.02em;
  margin-bottom: 2.5rem;
}

.accordion {
  border-top: 1px solid var(--border);
}

.acc-item {
  border-bottom: 1px solid var(--border);
}

.acc-trigger {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.25rem 0;
  background: none;
  border: none;
  cursor: pointer;
  font-family: 'Outfit', sans-serif;
  font-size: 0.95rem;
  font-weight: 600;
  color: var(--text);
  text-align: left;
