import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Lenis from 'lenis';

gsap.registerPlugin(ScrollTrigger);

initDemoShell({
  title: 'Meridian — E-Learning Platform',
  category: 'pages',
  tech: ['gsap', 'scroll-trigger', 'lenis', 'accordion', 'canvas-2d', 'outfit'],
});

const reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

// ── Lenis ─────────────────────────────────────────────────────────────────────
const lenis = new Lenis({ lerp: 0.1, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

// ── Nav ───────────────────────────────────────────────────────────────────────
// Nav is always visible (solid), no scroll-triggered changes needed

// ── Hero entrance ─────────────────────────────────────────────────────────────
if (!reduced) {
  gsap.set(['.hero-badge', '.hero-h1', '.hero-p', '.hero-actions', '.hero-stats'], { opacity: 0, y: 20 });
  gsap.set('.progress-card', { opacity: 0, x: 30 });
  gsap.set('.skill-badge', { opacity: 0, scale: 0.8 });

  gsap.timeline({ delay: 0.3, defaults: { ease: 'expo.out' } })
    .to('.hero-badge',   { opacity: 1, y: 0, duration: 0.6 })
    .to('.hero-h1',      { opacity: 1, y: 0, duration: 0.9 }, '-=0.3')
    .to('.hero-p',       { opacity: 1, y: 0, duration: 0.7 }, '-=0.5')
    .to('.hero-actions', { opacity: 1, y: 0, duration: 0.6 }, '-=0.4')
    .to('.hero-stats',   { opacity: 1, y: 0, duration: 0.6 }, '-=0.3')
    .to('.progress-card',{ opacity: 1, x: 0, duration: 0.9, ease: 'back.out(1.5)' }, 0.5)
    .to('.skill-badge',  { opacity: 1, scale: 1, duration: 0.5, stagger: 0.1, ease: 'back.out(2)' }, 0.8);
}

// ── Hero stat counters ────────────────────────────────────────────────────────
document.querySelectorAll('.hs-num').forEach((el) => {
  const target = parseFloat(el.dataset.target);
  const suffix = el.dataset.suffix || '';
  if (!reduced) {
    ScrollTrigger.create({
      trigger: '.hero-stats',
      start: 'top 85%',
      end: 'top 30%',
      onUpdate: (self) => {
        const val = Math.round(target * self.progress);
        el.textContent = val.toLocaleString() + suffix;
      },
    });
  } else {
    el.textContent = target.toLocaleString() + suffix;
  }
});

// ── Progress card animation ───────────────────────────────────────────────────
if (!reduced) {
  // Animate progress bar to 37%
  gsap.to('#pc-fill', {
    width: '37%',
    duration: 1.5,
    ease: 'expo.out',
    delay: 1.2,
    onUpdate: function () {
      const w = parseFloat(this.targets()[0].style.width);
      document.getElementById('pc-pct').textContent = Math.round(w) + '%';
    },
  });

  // Floating skill badges gently float
  document.querySelectorAll('.skill-badge').forEach((badge, i) => {
    gsap.to(badge, {
      y: -8 + (i % 2) * 16,
      duration: 2 + i * 0.3,
      repeat: -1,
      yoyo: true,
      ease: 'sine.inOut',
      delay: i * 0.5,
    });
  });
} else {
  document.getElementById('pc-fill').style.width = '37%';
  document.getElementById('pc-pct').textContent = '37%';
}

// ── Skill tree canvas ─────────────────────────────────────────────────────────
const treeCanvas = document.getElementById('tree-canvas');
if (treeCanvas) {
  const tctx = treeCanvas.getContext('2d');
  function drawTree() {
    treeCanvas.width = treeCanvas.offsetWidth;
    treeCanvas.height = treeCanvas.offsetHeight;

    const COL_W = 110;
    const ROW_H = 60;
    const X_OFF = 60; // center of first col

    // Connections: [fromCol, fromRow, toCol, toRow, done]
    const connections = [
      [2, 1, 1, 2, true], [2, 1, 3, 2, true],
      [1, 2, 2, 3, false], [3, 2, 2, 3, false],
      [2, 3, 1, 4, false], [2, 3, 3, 4, false],
      [1, 4, 2, 5, false], [3, 4, 2, 5, false],
      [2, 5, 1, 6, false], [2, 5, 3, 6, false],
    ];

    connections.forEach(([c1, r1, c2, r2, done]) => {
      const x1 = (c1 - 1) * COL_W + X_OFF + 50;
      const y1 = (r1 - 1) * ROW_H + 28;
      const x2 = (c2 - 1) * COL_W + X_OFF + 50;
      const y2 = (r2 - 1) * ROW_H + 28;

      tctx.beginPath();
      tctx.moveTo(x1, y1);
      tctx.bezierCurveTo(x1, (y1 + y2) / 2, x2, (y1 + y2) / 2, x2, y2);
      tctx.strokeStyle = done ? 'rgba(5, 150, 105, 0.5)' : 'rgba(255, 255, 255, 0.08)';
      tctx.lineWidth = 1.5;
      tctx.stroke();
    });
  }
  drawTree();
  window.addEventListener('resize', drawTree);
}

// ── Course progress bars animate on scroll ────────────────────────────────────
if (!reduced) {
  document.querySelectorAll('.cc-progress-fill').forEach((fill, i) => {
    gsap.to(fill, {
      scaleX: 1,
      duration: 1,
      ease: 'expo.out',
      delay: i * 0.08,
      scrollTrigger: {
        trigger: '.courses-grid',
        start: 'top 75%',
        toggleActions: 'play none none reverse',
      },
    });
  });
}

// ── Course cards reveal ───────────────────────────────────────────────────────
if (!reduced) {
  document.querySelectorAll('.course-card').forEach((card, i) => {
    gsap.set(card, { opacity: 0, y: 30 });
    gsap.to(card, {
      opacity: 1, y: 0,
      duration: 0.8,
      ease: 'expo.out',
      delay: i * 0.08,
      scrollTrigger: {
        trigger: '.courses-grid',
        start: 'top 75%',
        toggleActions: 'play none none reverse',
      },
    });
  });
}

// ── Filter tabs ───────────────────────────────────────────────────────────────
document.querySelectorAll('.ft-btn').forEach((btn) => {
  btn.addEventListener('click', () => {
    document.querySelectorAll('.ft-btn').forEach((b) => b.classList.remove('active'));
    btn.classList.add('active');
    // In a real implementation: filter .course-card by data-category
    // Here we just toggle the active class for the demo
  });
});

// ── Pricing cards reveal ──────────────────────────────────────────────────────
if (!reduced) {
  gsap.set('.price-card', { opacity: 0, y: 25 });
  gsap.to('.price-card', {
    opacity: 1, y: 0,
    duration: 0.8,
    stagger: 0.12,
    ease: 'expo.out',
    scrollTrigger: {
      trigger: '.pricing-grid',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

// ── Skill tree nodes reveal ───────────────────────────────────────────────────
if (!reduced) {
  gsap.set('.st-node', { opacity: 0, scale: 0.8 });
  gsap.to('.st-node', {
    opacity: 1, scale: 1,
    duration: 0.5,
    stagger: 0.07,
    ease: 'back.out(1.5)',
    scrollTrigger: {
      trigger: '.skill-tree',
      start: 'top 70%',
      toggleActions: 'play none none reverse',
    },
  });
}

// ── FAQ Accordion ─────────────────────────────────────────────────────────────
const DURATION = reduced ? 0 : 0.4;

document.querySelectorAll('#faq-acc .acc-trigger').forEach((trigger) => {
  const item = trigger.closest('.acc-item');
  const body = item.querySelector('.acc-body');
  const arrow = trigger.querySelector('.at-arrow');
  gsap.set(body, { height: 0, overflow: 'hidden' });

  trigger.addEventListener('click', () => {
    const isOpen = item.classList.contains('is-open');

    // Close all
    document.querySelectorAll('#faq-acc .acc-item').forEach((other) => {
      if (other !== item && other.classList.contains('is-open')) {
        other.classList.remove('is-open');
        other.querySelector('.acc-trigger').setAttribute('aria-expanded', 'false');
        gsap.to(other.querySelector('.acc-body'), { height: 0, duration: DURATION, ease: 'expo.in', overwrite: true });
        gsap.to(other.querySelector('.at-arrow'), { rotation: 0, duration: DURATION * 0.7, ease: 'power2.in', overwrite: true });
      }
    });

    if (isOpen) {
      item.classList.remove('is-open');
      trigger.setAttribute('aria-expanded', 'false');
      gsap.to(body, { height: 0, duration: DURATION, ease: 'expo.in', overwrite: true });
      gsap.to(arrow, { rotation: 0, duration: DURATION * 0.7, ease: 'power2.in', overwrite: true });
    } else {
      item.classList.add('is-open');
      trigger.setAttribute('aria-expanded', 'true');
      gsap.to(body, { height: 'auto', duration: DURATION, ease: 'expo.out', overwrite: true });
      gsap.to(arrow, { rotation: 45, duration: DURATION, ease: 'back.out(2)', overwrite: true });
    }
  });
});

// ── Motion toggle ─────────────────────────────────────────────────────────────
window.addEventListener('motion-preference', (e) => {
  gsap.globalTimeline.paused(e.detail.reduced);
});

snippets.css:
/* ── Demo 49: Video Scroll Scrub ── */
/* Dark cinematic palette + clean system font */

:root {
  --bg: #060608;
  --panel: #0e0e14;
  --border: #1c1c28;
  --text: #f0eee8;
  --muted: #7a7888;
  --accent: #e8c060;   /* warm amber for progress/labels */
  --accent2: #60b8e8;  /* cyan for code */
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

html { scroll-behavior: auto; } /* GSAP handles scroll, not native */

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: var(--bg);
  color: var(--text);
  -webkit-font-smoothing: antialiased;
}

/* ── Labels ── */
.label {
  display: block;
  font-size: 0.68rem;
  font-weight: 700;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

code {
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.8rem;
  color: var(--accent2);
  background: rgba(96, 184, 232, 0.08);
  padding: 0.2em 0.5em;
  border-radius: 3px;
  display: inline-block;
  margin-top: 0.6rem;
}

/* ── Intro ── */
.intro {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 3rem 2rem;
  background: radial-gradient(ellipse at 50% 60%, rgba(232, 192, 96, 0.06) 0%, transparent 65%);
}

.intro-inner {
  max-width: 600px;
}

.intro h1 {
  font-size: clamp(3.5rem, 9vw, 7rem);
  font-weight: 800;
  line-height: 1.0;
  letter-spacing: -0.03em;
  margin-bottom: 1.5rem;
}

.intro h1 em {
  font-style: normal;
  color: var(--accent);
}

.intro p {
  font-size: 1.05rem;
  color: var(--muted);
  line-height: 1.8;
  margin-bottom: 3rem;
}

.scroll-hint {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
}

.scroll-hint span {
  font-size: 0.72rem;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.sh-arrow {
  font-size: 1.2rem;
  color: var(--accent);
  animation: bounce-arrow 1.5s ease-in-out infinite;
}

@keyframes bounce-arrow {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(8px); }
}

/* ── Scrub section ── */
.scrub-section {
  position: relative;
}

.scrub-sticky {
  position: sticky;
  top: 0;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.scrub-spacer {
  height: 400vh;  /* scroll distance = scrub range */
}

/* ── Video / Canvas ── */
.video-wrap {
  position: relative;
  width: 100%;
  max-width: 1100px;
  aspect-ratio: 16/9;
  background: var(--panel);
  overflow: hidden;
}

#video-canvas {
  width: 100%;
  height: 100%;
  display: block;
}

/* Fallback gradient animation (simulates video) */
.video-fallback {
  position: absolute;
  inset: 0;
}

.vf-frame {
  width: 100%;
  height: 100%;
  background: linear-gradient(
    135deg,
    hsl(220, 30%, 8%) 0%,
    hsl(240, 25%, 12%) 25%,
    hsl(200, 20%, 10%) 50%,
    hsl(260, 30%, 8%) 75%,
    hsl(220, 30%, 8%) 100%
  );
  /* Hue will be animated via CSS variable */
  --hue: 220;
}

/* ── Overlay UI ── */
.scrub-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 2rem;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
  display: flex;
  align-items: flex-end;
  justify-content: space-between;
  gap: 1.5rem;
}

.so-progress {
  flex: 1;
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.so-bar {
  flex: 1;
  height: 2px;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 1px;
  overflow: hidden;
}

.so-fill {
  height: 100%;
  width: 0%;
  background: var(--accent);
  box-shadow: 0 0 6px var(--accent);
  border-radius: 1px;
  transition: width 0.05s linear;
}

.so-time {
  font-family: 'SF Mono', monospace;
  font-size: 0.78rem;
  color: rgba(255,255,255,0.6);
  white-space: nowrap;
  min-width: 36px;
}

.so-caption {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 0.2rem;
}

.sc-label {
  font-size: 0.6rem;
  font-weight: 700;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--accent);
}

.sc-text {
  font-size: 0.9rem;
  color: rgba(255,255,255,0.8);
  font-weight: 500;
}

/* ── Chapters / How it works ── */
.chapters-section {
  padding: 6rem 3rem;
  max-width: 900px;
  margin: 0 auto;
}

.chapters-section h2 {
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 800;
  line-height: 1.15;
  letter-spacing: -0.02em;
  margin-bottom: 3.5rem;
}

.chapters {
  display: flex;
  flex-direction: column;
  gap: 0;
}

.chapter {
  display: grid;
  grid-template-columns: 60px 1fr;
  gap: 1.5rem;
  padding: 2rem 0;
  border-bottom: 1px solid var(--border);
}

.chapter:first-child {
  border-top: 1px solid var(--border);
}

.ch-num {
  font-size: 0.72rem;
  font-weight: 700;
  color: var(--accent);
  letter-spacing: 0.05em;
  padding-top: 3px;
}

.ch-content h3 {
  font-size: 1.05rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
}

.ch-content p {
  font-size: 0.9rem;
  color: var(--muted);
  line-height: 1.75;
}

/* ── Responsive ── */
@media (max-width: 768px) {
  .scrub-overlay { padding: 1rem; }
  .so-caption { display: none; }
  .chapters-section { padding: 4rem 1.5rem; }
  .chapter { grid-template-columns: 40px 1fr; }
}

/* ── Reduced motion ── */
html.reduced-motion .sh-arrow {
  animation: none;
}
html.reduced-motion .so-fill {
  transition: none;
}

