    delay: 0.3,
    scrollTrigger: {
      trigger: '.about-section',
      start: 'top 70%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.set('.about-content > *', { opacity: 0, y: 22 });
  gsap.to('.about-content > *', {
    opacity: 1,
    y: 0,
    duration: 0.8,
    stagger: 0.1,
    ease: 'expo.out',
    delay: 0.15,
    scrollTrigger: {
      trigger: '.about-section',
      start: 'top 70%',
      toggleActions: 'play none none reverse',
    },
