}

// ── Contact section reveal ────────────────────────────────────────────────────
if (!reduced) {
  gsap.set('.contact-left > *', { opacity: 0, y: 20 });
  gsap.to('.contact-left > *', {
    opacity: 1,
    y: 0,
    duration: 0.8,
    stagger: 0.1,
    ease: 'expo.out',
