/* ═══════════════════════════════════════════════════════════════════
   35-gamedev-portfolio — styles.css
   Retro pixel art aesthetic, CRT scanlines, HUD elements
═══════════════════════════════════════════════════════════════════ */

/* ── Google Font: Press Start 2P ──────────────────────────────── */
@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');

/* ── Custom Properties ─────────────────────────────────────────── */
:root {
  --bg:           #0f0f1a;
  --text:         #e8e8f0;
  --accent:       #ffdd57;
  --accent-2:     #ff6b9d;
  --accent-3:     #4ecdc4;
  --accent-4:     #ff4757;
  --muted:        #4a4a6a;
  --border:       #1e1e3a;
  --panel:        #141428;
  --panel-2:      #0c0c1e;
  --scanline:     rgba(0, 0, 0, 0.15);
  --pixel-font:   'Press Start 2P', monospace;
  --hero-font:    Impact, 'Arial Black', 'Haettenschweiler', sans-serif;
}

/* ── Reset & Base ──────────────────────────────────────────────── */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

html {
  scroll-behavior: smooth;
  font-size: 16px;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: var(--pixel-font);
  font-size: 8px;
  line-height: 1.8;
  overflow-x: hidden;
}

/* ── CRT Scanlines ─────────────────────────────────────────────── */
body::after {
  content: '';
  position: fixed;
  inset: 0;
  background: repeating-linear-gradient(
    to bottom,
    transparent 0px,
    transparent 2px,
    rgba(0, 0, 0, 0.15) 2px,
    rgba(0, 0, 0, 0.15) 4px
  );
  pointer-events: none;
  z-index: 9999;
  animation: crtFlicker 8s steps(1) infinite;
}

/* Subtle screen vignette */
body::before {
  content: '';
  position: fixed;
  inset: 0;
  background: radial-gradient(ellipse at center, transparent 60%, rgba(0,0,0,0.6) 100%);
  pointer-events: none;
  z-index: 9998;
}

@keyframes crtFlicker {
  0%, 97%, 100% { opacity: 1; }
  98% { opacity: 0.97; }
  99% { opacity: 0.99; }
}

/* ── Blink ─────────────────────────────────────────────────────── */
.blink { animation: blink 1s steps(1) infinite; }
@keyframes blink {
  0%, 100% { opacity: 1; }
  50%       { opacity: 0; }
}

/* ── Loading Overlay ───────────────────────────────────────────── */
.loading-overlay {
  position: fixed;
  inset: 0;
  background: var(--bg);
  z-index: 10000;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: opacity 0.4s ease;
}
.loading-overlay.hidden {
  opacity: 0;
  pointer-events: none;
}

.loading-inner {
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
}

.loading-logo {
  font-family: var(--pixel-font);
  font-size: 18px;
  color: var(--accent);
  text-shadow: 3px 3px 0 rgba(255, 221, 87, 0.3);
  animation: pulse 1s ease-in-out infinite alternate;
}

.loading-text {
  font-family: var(--pixel-font);
  font-size: 10px;
  color: var(--text);
  letter-spacing: 0.2em;
}

.loading-dots::after {
  content: '';
  animation: dots 1.5s steps(4) infinite;
}
@keyframes dots {
  0%  { content: ''; }
  25% { content: '.'; }
  50% { content: '..'; }
  75% { content: '...'; }
}

.loading-bar-wrap {
  width: 240px;
  height: 16px;
  border: 3px solid var(--accent);
  background: var(--panel-2);
  image-rendering: pixelated;
}
.loading-bar {
  height: 100%;
  width: 0%;
  background: var(--accent);
  transition: width 0.05s steps(1);
  box-shadow: 0 0 8px var(--accent);
}

.loading-pct {
  font-family: var(--pixel-font);
  font-size: 8px;
  color: var(--accent);
}

/* ── Pixel Nav ─────────────────────────────────────────────────── */
.pixel-nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: rgba(15, 15, 26, 0.92);
  border-bottom: 2px solid var(--border);
  backdrop-filter: blur(4px);
  transform: translateY(-100%);
  transition: transform 0.3s steps(6);
}
.pixel-nav.visible { transform: translateY(0); }

.nav-inner {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 12px 24px;
  max-width: 1100px;
  margin: 0 auto;
}

.nav-logo {
  font-family: var(--pixel-font);
  font-size: 12px;
  color: var(--accent);
  text-shadow: 2px 2px 0 rgba(255,221,87,0.4);
  letter-spacing: 0.1em;
}

.nav-links {
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
}

.nav-link {
  font-family: var(--pixel-font);
  font-size: 7px;
  color: var(--muted);
  text-decoration: none;
  letter-spacing: 0.1em;
  transition: color 0.1s, text-shadow 0.1s;
  padding: 4px 6px;
}
.nav-link:hover,
.nav-link.active {
  color: var(--accent);
  text-shadow: 0 0 8px var(--accent);
}

/* ── Hero ──────────────────────────────────────────────────────── */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.hero-canvas {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  display: block;
}

/* HUD corners */
.hud-corner {
  position: absolute;
  font-family: var(--pixel-font);
  font-size: 7px;
  z-index: 10;
}
.hud-tl { top: 80px; left: 24px; }
.hud-tr { top: 80px; right: 24px; text-align: right; }

.hud-hp, .hud-mp { margin-bottom: 10px; }
.hud-label {
  color: var(--accent-4);
  display: inline-block;
  width: 24px;
  margin-right: 6px;
}
.hud-bar {
  display: inline-block;
  width: 100px;
  height: 8px;
  background: var(--border);
  border: 1px solid var(--muted);
  vertical-align: middle;
  margin-right: 6px;
}
.hud-bar-fill {
  height: 100%;
  animation: hudPulse 2s ease-in-out infinite alternate;
}
.hud-bar-hp { width: 100%; background: var(--accent-4); }
.hud-bar-mp { width: 88%; background: var(--accent-3); }

@keyframes hudPulse {
  from { filter: brightness(1); }
  to   { filter: brightness(1.3); }
}

.hud-val { color: var(--text); }

.hud-xp-label { color: var(--accent); margin-bottom: 4px; }
.hud-xp-bar-wrap {
  width: 120px;
  height: 8px;
  background: var(--border);
  border: 1px solid var(--muted);
  margin-bottom: 4px;
}
.hud-xp-bar {
  width: 73%;
  height: 100%;
  background: var(--accent);
  box-shadow: 0 0 6px var(--accent);
}
.hud-level { color: var(--accent); }
.hud-level-num { color: var(--accent-2); }

/* Hero content */
.hero-content {
  position: relative;
  z-index: 10;
  text-align: center;
  padding: 24px;
}

.hero-eyebrow {
  font-family: var(--pixel-font);
  font-size: 9px;
  color: var(--accent-3);
  letter-spacing: 0.3em;
  margin-bottom: 24px;
  text-shadow: 0 0 12px var(--accent-3);
}

.hero-name {
  font-family: var(--hero-font);
  font-size: clamp(3.5rem, 10vw, 8rem);
  font-weight: 900;
  font-style: italic;
  letter-spacing: 0.05em;
  color: var(--accent);
  /* Chunky pixel shadow simulation */
  text-shadow:
    3px 3px 0 #b89c00,
    6px 6px 0 rgba(184, 156, 0, 0.5),
    0 0 40px rgba(255, 221, 87, 0.3);
  line-height: 0.95;
  margin-bottom: 20px;
  text-transform: uppercase;
}

.hero-subtitle {
  font-family: var(--pixel-font);
  font-size: 10px;
  color: var(--text);
  letter-spacing: 0.25em;
  margin-bottom: 28px;
  opacity: 0.85;
}

.hero-tags {
  display: flex;
  gap: 12px;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 60px;
}

.hero-tag {
  font-family: var(--pixel-font);
  font-size: 7px;
  padding: 6px 10px;
  border: 2px solid var(--accent-2);
  color: var(--accent-2);
  letter-spacing: 0.1em;
  background: rgba(255, 107, 157, 0.08);
}

.hero-scroll-cue {
  font-family: var(--pixel-font);
  font-size: 8px;
  color: var(--accent);
  letter-spacing: 0.1em;
}

/* Score display */
.hud-score {
  position: absolute;
  bottom: 24px;
  right: 24px;
  font-family: var(--pixel-font);
  font-size: 8px;
  text-align: right;
  z-index: 10;
}
.score-label { color: var(--muted); margin-bottom: 4px; }
.score-val {
  font-size: 14px;
  color: var(--accent);
  text-shadow: 0 0 10px var(--accent);
  letter-spacing: 0.1em;
}

/* ── Sections (shared) ─────────────────────────────────────────── */
.section {
  padding: 100px 24px;
  position: relative;
}

.section-inner {
  max-width: 960px;
  margin: 0 auto;
}

.section-header-pixel {
  display: flex;
  align-items: baseline;
  gap: 16px;
  margin-bottom: 56px;
  padding-bottom: 20px;
  border-bottom: 3px solid var(--border);
  flex-wrap: wrap;
}

.section-num {
  font-family: var(--pixel-font);
  font-size: 9px;
  color: var(--accent-2);
}

.section-title {
  font-family: var(--pixel-font);
  font-size: clamp(14px, 3vw, 20px);
  color: var(--text);
  text-shadow: 2px 2px 0 rgba(255,255,255,0.1);
}

.section-deco {
  font-family: var(--pixel-font);
  font-size: 7px;
  color: var(--muted);
  margin-left: auto;
}

/* ── Stats Section ─────────────────────────────────────────────── */
.stats-section { background: var(--panel-2); }

.stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 24px;
  margin-bottom: 48px;
}

.stat-card {
  background: var(--panel);
  border: 2px solid var(--border);
  padding: 20px;
  transition: border-color 0.2s, transform 0.1s steps(2);
}
.stat-card:hover {
  border-color: var(--accent);
  transform: translate(-2px, -2px);
}

.stat-row {
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 12px;
}

.stat-abbr {
  font-family: var(--pixel-font);
  font-size: 10px;
  color: var(--accent);
  min-width: 36px;
  text-shadow: 0 0 8px rgba(255, 221, 87, 0.5);
}

.stat-name {
  font-family: var(--pixel-font);
  font-size: 7px;
  color: var(--text);
  flex: 1;
  letter-spacing: 0.05em;
}

.stat-num {
  font-family: var(--pixel-font);
  font-size: 10px;
  color: var(--accent-3);
  min-width: 28px;
  text-align: right;
}

.stat-bar-wrap {
  width: 100%;
  height: 12px;
  background: var(--border);
  border: 2px solid var(--muted);
  margin-bottom: 10px;
  position: relative;
  overflow: hidden;
}

.stat-bar {
  height: 100%;
  width: 0%;
  background: linear-gradient(90deg, var(--accent-3), var(--accent));
  transition: none;
  position: relative;
}
/* Pixel block pattern on bar fill */
.stat-bar::after {
  content: '';
  position: absolute;
  inset: 0;
  background: repeating-linear-gradient(
    90deg,
    transparent 0px,
    transparent 10px,
    rgba(0,0,0,0.2) 10px,
    rgba(0,0,0,0.2) 12px
  );
}

.stat-desc {
  font-family: var(--pixel-font);
  font-size: 6px;
  color: var(--muted);
  line-height: 1.9;
}

/* XP progress bar */
.xp-progress {
  background: var(--panel);
  border: 2px solid var(--border);
  padding: 20px;
  display: flex;
  align-items: center;
  gap: 20px;
  flex-wrap: wrap;
}
.xp-label {
  font-family: var(--pixel-font);
  font-size: 7px;
  color: var(--accent);
  min-width: 180px;
}
.xp-bar-outer {
  flex: 1;
  min-width: 200px;
  height: 16px;
  background: var(--border);
  border: 2px solid var(--muted);
