}
.xp-bar-inner::after {
  content: '';
  position: absolute;
  inset: 0;
  background: repeating-linear-gradient(
    90deg,
    transparent 0px,
    transparent 14px,
    rgba(0,0,0,0.25) 14px,
    rgba(0,0,0,0.25) 16px
