}

.cart-sub {
  font-family: var(--pixel-font);
  font-size: 6px;
  color: rgba(0,0,0,0.6);
  letter-spacing: 0.05em;
}

.cart-platforms {
  font-size: 16px;
  margin-top: 10px;
}

.cart-bottom {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
