}
.contact-link-val {
  font-family: var(--pixel-font);
  font-size: 7px;
  color: var(--accent-3);
}

/* Email CTA */
.contact-email {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
}
.contact-email-label {
  font-family: var(--pixel-font);
  font-size: 7px;
  color: var(--muted);
  letter-spacing: 0.1em;
}
.contact-email-addr {
  font-family: var(--pixel-font);
  font-size: 9px;
  color: var(--accent);
  text-decoration: none;
  text-shadow: 0 0 10px rgba(255, 221, 87, 0.5);
  letter-spacing: 0.05em;
}

/* Credits */
.credits {
  border-top: 2px solid var(--border);
  padding-top: 24px;
  width: 100%;
  text-align: center;
  display: flex;
  flex-direction: column;
  gap: 8px;
}
.credits-line {
  font-family: var(--pixel-font);
  font-size: 6px;
  color: var(--muted);
  letter-spacing: 0.1em;
}
.credits-line.muted { color: var(--border); }

/* ── View Transition overlay ───────────────────────────────────── */
.vt-overlay {
  position: fixed;
  inset: 0;
  background: var(--bg);
  z-index: 5000;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.15s steps(3);
}
.vt-overlay.active { opacity: 1; }

.vt-text {
  font-family: var(--pixel-font);
  font-size: 14px;
  color: var(--accent);
  letter-spacing: 0.2em;
  text-shadow: 0 0 16px rgba(255,221,87,0.6);
}

/* ── View Transition API ───────────────────────────────────────── */
@keyframes vt-slide-in {
  from { opacity: 0; transform: translateY(8px); }
  to   { opacity: 1; transform: translateY(0); }
}
@keyframes vt-slide-out {
  from { opacity: 1; }
  to   { opacity: 0; }
}
::view-transition-old(root) {
  animation: vt-slide-out 0.2s steps(4) both;
}
::view-transition-new(root) {
  animation: vt-slide-in 0.2s steps(4) 0.2s both;
}

/* ── Pulse animation ───────────────────────────────────────────── */
@keyframes pulse {
  from { opacity: 0.7; transform: scale(0.98); }
  to   { opacity: 1;   transform: scale(1); }
}

/* ── Reduced motion overrides ──────────────────────────────────── */
@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
  .blink { animation: none; }
  body::after { animation: none; }
  .pixel-btn { transition: none; }
  .game-card, .inv-item, .stat-card { transition: none; }
}

/* ── Responsive ────────────────────────────────────────────────── */
@media (max-width: 600px) {
  .hud-corner { display: none; }
  .hud-score  { display: none; }
  .nav-links { gap: 10px; }
  .nav-link { font-size: 6px; }
  .section { padding: 64px 16px; }
  .section-title { font-size: 11px; }
  .hero-eyebrow { font-size: 7px; }
  .contact-title { font-size: 14px; }
  .contact-sub { font-size: 11px; }
  .games-grid { grid-template-columns: 1fr; }
  .stats-grid { grid-template-columns: 1fr; }
  .inventory-grid { grid-template-columns: 1fr; }
}

