  [0,3,3,6,6,3,3,0],   // 7 — lower torso
];

// Leg frames — 4 rows, cycled across 8 animation frames
const LEG_FRAMES = [
  // frame 0: stand
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,0,0,3,0],[0,4,0,0,0,0,4,0]],
  // frame 1: step right fwd
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,0,3,3,0,3,0,0],[0,0,4,4,0,4,0,0]],
  // frame 2: step right
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,0,0,3,0,3,0,0],[0,0,0,4,0,4,0,0]],
  // frame 3: step right back
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,0,3,0,0,3,3,0],[0,0,4,0,0,4,4,0]],
  // frame 4: stand (mirror)
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,0,0,3,0],[0,4,0,0,0,0,4,0]],
  // frame 5: step left fwd
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,3,3,0,0],[0,4,0,0,4,4,0,0]],
  // frame 6: step left
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,3,0,0,0],[0,4,0,0,4,0,0,0]],
  // frame 7: step left back
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,3,0,0,0,3,0],[0,4,4,0,0,0,4,0]],
];

function drawPixelChar(cx, cy, frameIndex) {
  const ps = PIXEL_SCALE;
  const fi = frameIndex % LEG_FRAMES.length;

  // upper body (shared)
  FRAME_TOPS.forEach((row, ry) => {
    row.forEach((colorKey, rx) => {
      if (!colorKey) return;
      ctx.fillStyle = COLORS[colorKey];
      ctx.fillRect(
        Math.round(cx + rx * ps - 4 * ps),
        Math.round(cy + ry * ps - 6 * ps),
        ps, ps
      );
    });
  });

  // legs (animated)
  LEG_FRAMES[fi].forEach((row, ry) => {
    row.forEach((colorKey, rx) => {
      if (!colorKey) return;
      ctx.fillStyle = COLORS[colorKey];
      ctx.fillRect(
        Math.round(cx + rx * ps - 4 * ps),
        Math.round(cy + (ry + 8) * ps - 6 * ps),
        ps, ps
      );
    });
  });
}

// ─── Animation loop ───────────────────────────────────────────────
let animFrame = 0;
let lastFrameTime = 0;
const FRAME_INTERVAL = 120; // ms per walk frame
let charY = 0; // slight bob

function animate(time) {
  if (!canvas.width || !canvas.height) {
    requestAnimationFrame(animate);
    return;
  }

  const w = canvas.width;
  const h = canvas.height;

  // Clear
  ctx.clearRect(0, 0, w, h);

  // Deep space gradient bg
  const grad = ctx.createLinearGradient(0, 0, 0, h);
  grad.addColorStop(0, '#0a0a1a');
  grad.addColorStop(1, '#0f0f2a');
  ctx.fillStyle = grad;
  ctx.fillRect(0, 0, w, h);

  // Stars
  drawStars(time / 1000);

  // Subtle ground grid
  if (!reducedMotion) {
    ctx.strokeStyle = 'rgba(78, 205, 196, 0.07)';
    ctx.lineWidth = 1;
    const gridSize = 40;
    const scrollOffset = (time * 0.02) % gridSize;
    for (let x = 0; x < w + gridSize; x += gridSize) {
      ctx.beginPath();
      ctx.moveTo(x - scrollOffset, h * 0.7);
      ctx.lineTo(x - scrollOffset, h);
      ctx.stroke();
    }
    for (let y = h * 0.7; y < h; y += gridSize * 0.5) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(w, y);
      ctx.stroke();
    }
  }

  // Advance walk frame
  if (!reducedMotion && time - lastFrameTime > FRAME_INTERVAL) {
    animFrame = (animFrame + 1) % 8;
    lastFrameTime = time;
  }

  // Character bob
  if (!reducedMotion) {
    charY = Math.sin(time / 500) * 3;
  }

  // Draw character at center, slightly below mid
  const charX = w / 2;
  const charCy = h * 0.65 + charY;
  drawPixelChar(charX, charCy, animFrame);

  // Glow under character
  const glowGrad = ctx.createRadialGradient(charX, charCy + 20, 0, charX, charCy + 20, 40);
  glowGrad.addColorStop(0, 'rgba(78, 205, 196, 0.15)');
  glowGrad.addColorStop(1, 'transparent');
  ctx.fillStyle = glowGrad;
  ctx.fillRect(charX - 40, charCy, 80, 40);

  requestAnimationFrame(animate);
}

// ─── Score counter ────────────────────────────────────────────────
const scoreEl = document.getElementById('scoreVal');
let score = 0;
function tickScore() {
  if (reducedMotion) return;
  score += Math.floor(Math.random() * 37 + 1);
  if (score > 999999) score = 0;
  scoreEl.textContent = String(score).padStart(6, '0');
  setTimeout(tickScore, 80 + Math.random() * 140);
}

// ─── Pixel Nav visibility ─────────────────────────────────────────
const nav = document.getElementById('pixelNav');
const heroSection = document.getElementById('hero');

ScrollTrigger.create({
  trigger: heroSection,
  start: 'bottom 80%',
  onEnter: () => nav.classList.add('visible'),
  onLeaveBack: () => nav.classList.remove('visible'),
});

// ─── Active nav link on scroll ────────────────────────────────────
const sections = ['hero', 'stats', 'games', 'tech', 'contact'];
sections.forEach((id) => {
  ScrollTrigger.create({
    trigger: `#${id}`,
    start: 'top 50%',
    end: 'bottom 50%',
    onToggle: ({ isActive }) => {
      const link = document.querySelector(`.nav-link[data-section="${id}"]`);
      if (link) link.classList.toggle('active', isActive);
    },
  });
});

// ─── View Transition helper ───────────────────────────────────────
const vtOverlay = document.getElementById('vtOverlay');

function triggerViewTransition(targetId) {
  if (reducedMotion) {
    document.getElementById(targetId)?.scrollIntoView({ behavior: 'smooth' });
    return;
  }

  const supportsVT = typeof document.startViewTransition === 'function';

  if (supportsVT) {
    document.startViewTransition(() => {
      document.getElementById(targetId)?.scrollIntoView({ behavior: 'instant' });
    });
  } else {
    // Fallback: manual overlay
    vtOverlay.classList.add('active');
    setTimeout(() => {
      document.getElementById(targetId)?.scrollIntoView({ behavior: 'instant' });
      setTimeout(() => vtOverlay.classList.remove('active'), 300);
    }, 250);
  }
}

// Nav link click handler
document.querySelectorAll('.nav-link').forEach((link) => {
  link.addEventListener('click', (e) => {
    e.preventDefault();
    const target = link.dataset.section;
    triggerViewTransition(target);
  });
});

// ─── Stat bars — animate on scroll enter ─────────────────────────
function initStatAnimations() {
  const statCards = document.querySelectorAll('.stat-card');

  statCards.forEach((card) => {
    const bar     = card.querySelector('.stat-bar');
    const numEl   = card.querySelector('.stat-num');
    const barWidth  = parseInt(bar?.dataset.width || '0', 10);
    const numTarget = parseInt(numEl?.dataset.target || '0', 10);

    ScrollTrigger.create({
      trigger: card,
      start: 'top 80%',
      once: true,
      onEnter: () => {
        if (reducedMotion) {
          if (bar)   bar.style.width = barWidth + '%';
          if (numEl) numEl.textContent = numTarget;
          return;
        }
        // Animate bar
        if (bar) {
          gsap.to(bar, {
            width: barWidth + '%',
            duration: 1.2,
            ease: 'steps(20)',
          });
        }
        // Count up number
        if (numEl) {
          gsap.to({ val: 0 }, {
            val: numTarget,
            duration: 1.4,
            ease: 'steps(20)',
            onUpdate: function () {
              numEl.textContent = Math.round(this.targets()[0].val);
            },
          });
        }
      },
    });
  });

  // XP bar
  const xpBar = document.querySelector('.xp-bar-inner');
  const xpNum = document.querySelector('.xp-pct-num');

  if (xpBar) {
    ScrollTrigger.create({
      trigger: xpBar,
      start: 'top 85%',
      once: true,
      onEnter: () => {
        const target = parseInt(xpBar.dataset.xp || '0', 10);
        if (reducedMotion) {
          xpBar.style.width = target + '%';
          if (xpNum) xpNum.textContent = target;
          return;
        }
        gsap.to(xpBar, { width: target + '%', duration: 1.5, ease: 'steps(25)' });
        if (xpNum) {
          gsap.to({ val: 0 }, {
            val: target,
            duration: 1.5,
            ease: 'steps(25)',
            onUpdate: function () {
              xpNum.textContent = Math.round(this.targets()[0].val);
            },
          });
        }
      },
    });
  }
}

// ─── Section reveal animations ────────────────────────────────────
function initSectionReveals() {
  if (reducedMotion) return;

  // Stat cards stagger
  gsap.from('.stat-card', {
    scrollTrigger: {
      trigger: '.stats-grid',
      start: 'top 85%',
    },
    y: 20,
    opacity: 0,
    duration: 0.4,
    ease: 'steps(4)',
    stagger: 0.08,
  });

  // Game cards
  gsap.from('.game-card', {
    scrollTrigger: {
      trigger: '.games-grid',
      start: 'top 85%',
    },
    y: 30,
    opacity: 0,
    duration: 0.5,
    ease: 'steps(5)',
    stagger: 0.12,
  });

  // Inventory items
  gsap.from('.inv-item', {
    scrollTrigger: {
      trigger: '.inventory-grid',
      start: 'top 85%',
    },
    scale: 0.9,
    opacity: 0,
    duration: 0.3,
    ease: 'steps(3)',
    stagger: 0.06,
  });

  // Contact section
  gsap.from('.contact-screen > *', {
    scrollTrigger: {
      trigger: '.contact-screen',
      start: 'top 85%',
    },
    y: 16,
    opacity: 0,
    duration: 0.4,
    ease: 'steps(4)',
    stagger: 0.1,
  });

  // Section headers
  document.querySelectorAll('.section-header-pixel').forEach((header) => {
    gsap.from(header, {
      scrollTrigger: { trigger: header, start: 'top 90%' },
      x: -20,
      opacity: 0,
      duration: 0.5,
      ease: 'steps(5)',
    });
  });
}

// ─── Hero entrance animation ──────────────────────────────────────
function animateHeroEntrance() {
  if (reducedMotion) return;

  const tl = gsap.timeline({ delay: 0.2 });
  tl.from('.hero-eyebrow', { opacity: 0, y: -10, duration: 0.4, ease: 'steps(4)' })
    .from('.hero-name',    { opacity: 0, y: 20,  duration: 0.5, ease: 'steps(5)' }, '-=0.1')
    .from('.hero-subtitle',{ opacity: 0,         duration: 0.3, ease: 'steps(3)' }, '-=0.1')
    .from('.hero-tags .hero-tag', {
      opacity: 0,
      y: 8,
      duration: 0.3,
      ease: 'steps(3)',
      stagger: 0.07,
    }, '-=0.1')
    .from('.hero-scroll-cue', { opacity: 0, duration: 0.4, ease: 'steps(4)' }, '-=0.1')
    .from('.hud-corner', { opacity: 0, duration: 0.3, ease: 'steps(3)', stagger: 0.15 }, '-=0.2')
    .from('.hud-score',  { opacity: 0, duration: 0.3, ease: 'steps(3)' }, '-=0.1');
}

// ─── "No" button easter egg ───────────────────────────────────────
const btnNo = document.getElementById('btnNo');
let noClicks = 0;
const noMessages = [
  '✕ ARE YOU SURE?',
  '✕ REALLY SURE?',
  '✕ FINAL ANSWER?',
  '✕ WELL OK THEN',
  '✕ YOUR LOSS!',
  '✕ ...JUST KIDDING!',
];
if (btnNo) {
  btnNo.addEventListener('click', () => {
    noClicks = Math.min(noClicks + 1, noMessages.length - 1);
    btnNo.textContent = noMessages[noClicks];
    if (noClicks === noMessages.length - 1) {
      setTimeout(() => {
        noClicks = 0;
        btnNo.textContent = '✕ NO / QUIT';
      }, 2000);
    }
  });
}

// ─── Keyboard: any key → scroll to stats ─────────────────────────
let hasScrolled = false;
function onAnyKey(e) {
  if (hasScrolled) return;
  if (['Tab', 'Shift', 'Control', 'Alt', 'Meta'].includes(e.key)) return;
  hasScrolled = true;
  triggerViewTransition('stats');
  window.removeEventListener('keydown', onAnyKey);
}
window.addEventListener('keydown', onAnyKey);

// ─── Boot sequence ────────────────────────────────────────────────
async function boot() {
  await runLoadingScreen();

  // Start canvas animation loop
  requestAnimationFrame(animate);

  // Start score counter
  tickScore();

  // GSAP animations
  initSectionReveals();
  initStatAnimations();
  animateHeroEntrance();
}

boot();

snippets.css:
:root {
  --page-bg: #faf8f4;
  --page-surface: #ffffff;
  --page-border: #e8e4dc;
  --page-text: #1a1a1a;
  --page-muted: #8a8478;
  --page-accent: #c4682b;
  --page-accent-dark: #8b4513;
  --page-sea: #2e7d9c;
  --page-sky: #87ceeb;
  --page-sand: #d4b896;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

body {
  background: var(--page-bg);
  color: var(--page-text);
  font-family: Georgia, 'Times New Roman', 'Playfair Display', serif;
  line-height: 1.8;
  overflow-x: hidden;
}

/* ── Demo shell overrides for light theme ── */
.demo-shell-back {
  background: rgba(255, 255, 255, 0.85) !important;
  border-color: rgba(0, 0, 0, 0.12) !important;
  color: var(--page-accent) !important;
}

.demo-shell-info {
  background: rgba(250, 248, 244, 0.95) !important;
  border-top-color: rgba(0, 0, 0, 0.08) !important;
  color: var(--page-muted) !important;
}

.demo-shell-info .demo-title { color: var(--page-text) !important; }

.demo-shell-info .demo-tag {
  background: rgba(196, 104, 43, 0.1) !important;
  border-color: rgba(196, 104, 43, 0.2) !important;
  color: var(--page-accent) !important;
}

.motion-toggle {
  background: rgba(255, 255, 255, 0.85) !important;
  border-color: rgba(0, 0, 0, 0.12) !important;
  color: var(--page-accent) !important;
}

/* ── Sections ── */
.section {
  position: relative;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 6rem 2rem;
}

/* ── Hero ── */
.hero-section {
  position: relative;
  overflow: hidden;
  text-align: center;
}

.hero-bg {
  position: absolute;
  inset: 0;
  background: linear-gradient(180deg,
    var(--page-sky) 0%,
    #5ba8c8 30%,
    var(--page-sea) 50%,
    var(--page-bg) 85%,
    var(--page-bg) 100%
  );
  z-index: 0;
}

.hero-content {
  position: relative;
  z-index: 1;
}

.hero-overline {
  display: block;
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 0.7rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 1.5rem;
  font-weight: 500;
}

.hero-title {
  font-size: clamp(3rem, 10vw, 7rem);
  font-weight: 700;
  color: #ffffff;
  line-height: 1;
  letter-spacing: -0.02em;
  text-shadow: 0 4px 40px rgba(0, 0, 0, 0.15);
  margin-bottom: 1rem;
}

.hero-rule {
  width: 60px;
  height: 2px;
  border: none;
  background: var(--page-accent);
  margin: 1.5rem auto;
  opacity: 0;
}

.hero-subtitle {
  font-size: clamp(1rem, 3vw, 1.5rem);
  color: rgba(255, 255, 255, 0.8);
  font-style: italic;
  font-weight: 400;
  opacity: 0;
}

.scroll-arrow {
  margin-top: 4rem;
  color: rgba(255, 255, 255, 0.5);
  opacity: 0;
  animation: scroll-float 2.5s ease-in-out infinite;
}

@keyframes scroll-float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(8px); }
}

.reduced-motion .scroll-arrow { animation: none; }

/* ── Prose Section ── */
.prose-section {
  min-height: auto;
  padding: 8rem 2rem;
}

.prose-column {
  max-width: 680px;
  width: 100%;
}

.prose-text {
  font-size: clamp(1rem, 2vw, 1.15rem);
  color: var(--page-muted);
  margin-bottom: 2rem;
  transition: color 0.3s ease;
}

.prose-text.revealed {
  color: var(--page-text);
}

.reduced-motion .prose-text {
  color: var(--page-text);
}

/* Drop cap */
.dropcap::first-letter {
  float: left;
  font-size: 3.5em;
  line-height: 0.8;
  padding-right: 0.1em;
  color: var(--page-accent);
  font-weight: 700;
}

/* Pull quote */
.pull-quote {
  border-left: 3px solid var(--page-accent);
  padding: 1rem 0 1rem 2rem;
  margin: 3rem 0;
}

.quote-text {
  font-size: clamp(1.1rem, 2.5vw, 1.35rem);
  font-style: italic;
  color: var(--page-accent-dark);
  line-height: 1.6;
}

/* ── Horizontal Gallery ── */
.gallery-section {
  position: relative;
  height: 400vh;
}

.gallery-track {
  position: sticky;
  top: 0;
  height: 100vh;
  display: flex;
  width: 400vw;
  overflow: hidden;
}

.gallery-panel {
  width: 100vw;
  height: 100vh;
  position: relative;
  flex-shrink: 0;
  overflow: hidden;
}

.panel-landscape {
  position: absolute;
  inset: 0;
}

/* CSS-only landscape compositions */
.panel-cliffs .panel-landscape {
  background:
    linear-gradient(180deg, var(--page-sky) 0%, #6bb3d4 40%, transparent 50%),
    linear-gradient(180deg, transparent 40%, var(--page-sea) 55%, #1a5f7a 100%);
}

.panel-cliffs .panel-landscape::before {
  content: '';
  position: absolute;
  bottom: 0;
  left: 10%;
  width: 35%;
  height: 75%;
  background: linear-gradient(160deg, #d4a574 0%, #a67c52 40%, #8b6342 100%);
  clip-path: polygon(20% 100%, 0% 40%, 15% 15%, 40% 0%, 65% 10%, 80% 25%, 100% 50%, 90% 100%);
}

.panel-cliffs .panel-landscape::after {
  content: '';
  position: absolute;
  bottom: 0;
  right: 5%;
  width: 25%;
  height: 60%;
  background: linear-gradient(170deg, #c49a6c 0%, #9e7a54 100%);
  clip-path: polygon(10% 100%, 0% 50%, 30% 10%, 60% 0%, 90% 20%, 100% 60%, 95% 100%);
}

.panel-sea .panel-landscape {
  background: linear-gradient(180deg, #6bb3d4 0%, var(--page-sea) 30%, #1a6b8a 60%, #144d65 100%);
}

.panel-sea .panel-landscape::before {
  content: '';
  position: absolute;
  bottom: 30%;
  left: 0;
  right: 0;
  height: 20%;
  background: rgba(255, 255, 255, 0.08);
  border-radius: 50% 50% 0 0 / 100% 100% 0 0;
  transform: scaleX(1.5);
}

.panel-sea .panel-landscape::after {
  content: '';
  position: absolute;
  bottom: 20%;
  left: -10%;
  right: -10%;
  height: 15%;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 50% 50% 0 0 / 100% 100% 0 0;
}

.panel-sunset .panel-landscape {
  background: linear-gradient(180deg,
    #2c1654 0%,
    #8b3a62 20%,
    #d4614a 40%,
    #f0a040 55%,
    #ffd080 65%,
    var(--page-sea) 80%,
    #1a5f7a 100%
  );
}

.panel-sunset .panel-landscape::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: radial-gradient(circle, #fff8e0 0%, #ffd060 40%, #f0a040 70%, transparent 100%);
  filter: blur(8px);
}

.panel-village .panel-landscape {
  background: linear-gradient(180deg,
    #2a1a3e 0%,
    #4a2a5e 25%,
    #8b5a3e 50%,
    #c49a6c 70%,
    var(--page-sand) 85%,
    var(--page-bg) 100%
  );
}

.panel-village .panel-landscape::before {
  content: '';
  position: absolute;
  bottom: 25%;
  left: 15%;
  width: 70%;
  height: 35%;
  background: #1a1a2e;
  clip-path: polygon(
    0% 100%, 2% 60%, 5% 40%, 8% 60%, 12% 30%, 15% 50%, 18% 20%, 22% 45%,
    25% 15%, 28% 35%, 32% 10%, 35% 25%, 38% 40%, 42% 20%, 45% 35%, 48% 5%,
    52% 30%, 55% 15%, 58% 40%, 62% 25%, 65% 45%, 68% 20%, 72% 50%, 75% 30%,
    78% 55%, 82% 35%, 85% 50%, 88% 40%, 92% 60%, 95% 45%, 100% 70%, 100% 100%
  );
  opacity: 0.7;
}

/* Tiny windows */
.panel-village .panel-landscape::after {
  content: '';
  position: absolute;
  bottom: 32%;
  left: 25%;
  width: 50%;
  height: 15%;
  background:
    radial-gradient(circle 2px at 10% 30%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 25% 50%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 40% 20%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 55% 60%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 70% 35%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 85% 45%, #ffd060 0%, transparent 100%);
}

.panel-caption {
  position: absolute;
  bottom: 4rem;
  left: 4rem;
  z-index: 2;
  display: flex;
  align-items: baseline;
  gap: 1rem;
  opacity: 0;
  transform: translateY(20px);
}

.reduced-motion .panel-caption {
  opacity: 1;
  transform: none;
}

.caption-number {
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.1em;
  color: rgba(255, 255, 255, 0.5);
}

.caption-text {
  font-size: clamp(1.2rem, 3vw, 1.8rem);
  color: #ffffff;
  font-weight: 600;
  text-shadow: 0 2px 16px rgba(0, 0, 0, 0.3);
}

/* Gallery progress bar */
.gallery-progress {
  position: fixed;
  bottom: 50px;
  left: 50%;
  transform: translateX(-50%);
  width: 200px;
  height: 2px;
  background: rgba(196, 104, 43, 0.2);
  border-radius: 1px;
  z-index: 10;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.gallery-progress.visible {
  opacity: 1;
}

.gallery-progress-fill {
  height: 100%;
  width: 0%;
  background: var(--page-accent);
  border-radius: 1px;
  transition: width 0.1s linear;
}

/* ── Stories Section (FLIP) ── */
.stories-section {
  min-height: auto;
  padding: 8rem 2rem;
}

.stories-heading {
  font-size: clamp(1.8rem, 5vw, 3rem);
  font-weight: 700;
  text-align: center;
  margin-bottom: 3rem;
  color: var(--page-text);
}

.stories-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
}

.story-card {
  background: var(--page-surface);
  border: 1px solid var(--page-border);
  border-radius: 12px;
  overflow: hidden;
  cursor: pointer;
  transition: box-shadow 0.3s ease;
}

.story-card:hover {
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.08);
}

.story-card.expanded {
  grid-column: 1 / -1;
  cursor: default;
}

.story-header {
  height: 120px;
}

.header-path {
  background: linear-gradient(135deg, #c49a6c 0%, #8b6342 60%, #5a3e2b 100%);
}

.header-lemon {
  background: linear-gradient(135deg, #ffd060 0%, #f0a040 50%, #c4682b 100%);
}

.header-fisher {
  background: linear-gradient(135deg, var(--page-sea) 0%, #1a5f7a 50%, #0e3d4f 100%);
}

.story-title {
  font-size: 1.2rem;
  font-weight: 700;
  padding: 1.25rem 1.5rem 0.5rem;
  color: var(--page-text);
}

.story-preview {
  font-size: 0.9rem;
  color: var(--page-muted);
  padding: 0 1.5rem 1.5rem;
  line-height: 1.6;
}

.story-full {
  display: none;
  padding: 0 1.5rem;
}

.story-card.expanded .story-full {
  display: block;
}

.story-full p {
  font-size: 0.95rem;
  color: var(--page-muted);
  margin-bottom: 1.25rem;
  line-height: 1.7;
}

.story-toggle {
  display: block;
  width: 100%;
  padding: 1rem 1.5rem;
  background: none;
  border: none;
  border-top: 1px solid var(--page-border);
  color: var(--page-accent);
  font: 600 0.8rem/1 'Inter', system-ui, sans-serif;
  cursor: pointer;
  text-align: left;
  transition: background 0.2s ease;
}

.story-toggle:hover {
  background: rgba(196, 104, 43, 0.05);
}

.story-card.expanded .story-toggle::after {
  content: ' (click to close)';
  color: var(--page-muted);
  font-weight: 400;
}

/* ── Closing ── */
.closing-section {
  padding: 10rem 2rem;
  text-align: center;
}

.closing-quote {
  font-size: clamp(1.5rem, 5vw, 3rem);
  font-weight: 700;
  font-style: italic;
  color: var(--page-text);
  max-width: 700px;
  margin-bottom: 3rem;
  line-height: 1.3;
}

.closing-meta {
  display: flex;
  gap: 1rem;
  justify-content: center;
  align-items: center;
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 0.75rem;
  color: var(--page-muted);
  text-transform: uppercase;
  letter-spacing: 0.08em;
  flex-wrap: wrap;
}

.meta-divider {
  opacity: 0.3;
}

/* ── Responsive ── */
@media (max-width: 768px) {
  .stories-grid {
    grid-template-columns: 1fr;
  }

  .section {
    padding: 4rem 1.25rem;
  }

  .panel-caption {
    left: 2rem;
    bottom: 3rem;
  }

  .prose-column {
    padding: 0;
  }
}

