  font-size: 0.78rem;
  font-family: "Trebuchet MS", "Segoe UI", sans-serif;
}

h1 {
  margin: 0;
  font-size: clamp(2.6rem, 8vw, 6rem);
  line-height: 0.92;
  letter-spacing: -0.02em;
}

.desc {
  margin: 0.7rem 0 0;
  font-family: "Trebuchet MS", "Segoe UI", sans-serif;
  color: var(--muted);
  max-width: 52ch;
}

.actions {
  margin-top: 1rem;
  display: flex;
  gap: 0.6rem;
  flex-wrap: wrap;
}

.buy,
.line {
  border-radius: 999px;
  padding: 0.58rem 0.9rem;
  cursor: pointer;
  font-weight: 700;
  font-family: "Trebuchet MS", "Segoe UI", sans-serif;
}

.buy {
  border: 0;
  background: #2c3f57;
  color: #f1f5ff;
}

.line {
  border: 1px solid #b5ac9d;
  background: transparent;
  color: #2d2f35;
}

.product-wrap {
  position: relative;
  display: grid;
  place-items: center;
  min-height: 420px;
}

.halo {
  position: absolute;
  width: 360px;
  height: 360px;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(83, 121, 170, 0.26), transparent 70%);
  filter: blur(2px);
}

.device {
  width: min(440px, 82%);
  aspect-ratio: 4 / 3;
  border-radius: 26px;
  border: 1px solid #c5bdaf;
  background:
    linear-gradient(165deg, #f9fafb, #d4d8de),
    radial-gradient(circle at 70% 30%, rgba(69, 99, 141, 0.2), transparent 55%);
  box-shadow: 0 28px 60px rgba(35, 40, 52, 0.16);
  transform-style: preserve-3d;
}

.spec-grid {
  margin: 0 0 2rem;
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 0.8rem;
}

.spec {
  border: 1px solid var(--line);
  border-radius: 14px;
  padding: 0.85rem;
  background: rgba(255,255,255,0.55);
}

.spec h2 {
  margin: 0;
  font-size: 1.25rem;
}

.spec p {
  margin: 0.45rem 0 0;
  color: var(--muted);
  font-family: "Trebuchet MS", "Segoe UI", sans-serif;
}

body.no-motion .halo,
body.no-motion .device {
  animation: none;
}

@media (max-width: 960px) {
  .hero { grid-template-columns: 1fr; min-height: 88vh; }
  .spec-grid { grid-template-columns: 1fr; }
}

