/* ══════════════════════════════════════════════════════════════
   REDUCED MOTION
   ══════════════════════════════════════════════════════════════ */
@media (prefers-reduced-motion: reduce) {
  .hero-scroll-line { animation: none; }
  .disciplines-track { animation-play-state: paused; }
  .hero-name-wrapper.is-hovered .hero-name,
  .hero-name-wrapper.is-hovered .hero-name-glitch--1,
