.reduced-motion .hero-scroll-line { animation: none; }
.reduced-motion .disciplines-track { animation-play-state: paused; }
.reduced-motion .hero-name-wrapper.is-hovered .hero-name { animation: none; }
.reduced-motion .hero-name-wrapper.is-hovered .hero-name-glitch--1,
.reduced-motion .hero-name-wrapper.is-hovered .hero-name-glitch--2 { opacity: 0 !important; }

/* ══════════════════════════════════════════════════════════════
   RESPONSIVE
   ══════════════════════════════════════════════════════════════ */
@media (max-width: 640px) {
  .media-gallery { grid-template-columns: 1fr; }
  .contact-bg-text { display: none; }
