  position: relative;
}

.hf-img--main {
  width: 100%;
  aspect-ratio: 3/4;
  background: linear-gradient(
    180deg,
    #2a2318 0%,
    #1a160e 30%,
    #121008 60%,
    #0e0c07 100%
  );
