  box-shadow: 0 30px 80px rgba(0,0,0,0.7);
  position: relative;
}

.hf-img--main::before {
  content: '';
  position: absolute;
  inset: 0;
  background:
    radial-gradient(ellipse at 60% 25%, rgba(200, 169, 110, 0.08) 0%, transparent 55%),
    radial-gradient(ellipse at 20% 80%, rgba(80, 60, 30, 0.2) 0%, transparent 40%);
