@keyframes pulse-gold {
  0%, 100% { opacity: 1; box-shadow: 0 0 0 0 rgba(200, 169, 110, 0.4); }
  50% { opacity: 0.7; box-shadow: 0 0 0 4px rgba(200, 169, 110, 0); }
