    y: 0,
    duration: 1.2,
    ease: 'expo.out',
    scrollTrigger: {
      trigger: '.contact-section',
      start: 'top 70%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.set('.cl-item', { opacity: 0, y: 12 });
  gsap.to('.cl-item', {
    opacity: 1,
    y: 0,
    duration: 0.8,
    stagger: 0.1,
    ease: 'expo.out',
    delay: 0.2,
    scrollTrigger: {
      trigger: '.contact-body',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.set('.contact-availability > *', { opacity: 0, y: 12 });
  gsap.to('.contact-availability > *', {
    opacity: 1,
    y: 0,
    duration: 0.8,
    stagger: 0.12,
    ease: 'expo.out',
    delay: 0.35,
    scrollTrigger: {
      trigger: '.contact-body',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

// ── Slow scroll‐driven parallax on hero image ─────────────────────────────────
if (!reduced) {
  gsap.to('.hf-img--main', {
    yPercent: -6,
    ease: 'none',
    scrollTrigger: {
      trigger: '.hero',
      start: 'top top',
      end: 'bottom top',
