*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

body {
  background: #030510;
  color: #f0f4fb;
  font-family: 'Inter', 'SF Pro Display', system-ui, sans-serif;
  min-height: 100vh;
  overflow-x: hidden;
}

#canvas-container {
  position: fixed;
  inset: 0;
  z-index: 0;
}

#canvas-container canvas {
  display: block;
  width: 100%;
  height: 100%;
}

.content {
  position: relative;
  z-index: 2;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 4rem 2rem;
}

.eyebrow {
  font-size: 0.7rem;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.18em;
  color: #86e8ff;
  margin-bottom: 1rem;
}

h1 {
  font-size: clamp(2.5rem, 7vw, 5rem);
  font-weight: 700;
  letter-spacing: -0.03em;
  line-height: 1.05;
  text-shadow: 0 0 60px rgba(134, 232, 255, 0.2);
}

.subtitle {
  font-size: clamp(0.9rem, 2vw, 1.05rem);
  color: #8a95a8;
  max-width: 500px;
  margin-top: 0.75rem;
  line-height: 1.6;
}

.info-cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 1rem;
  max-width: 760px;
  width: 100%;
  margin-top: 3rem;
}

.info-card {
  background: rgba(18, 26, 43, 0.5);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border: 1px solid rgba(134, 232, 255, 0.1);
  border-radius: 14px;
  padding: 1.5rem;
  text-align: left;
}

.info-card h3 {
  font-size: 0.9rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #86e8ff;
}

.info-card p {
  font-size: 0.82rem;
  color: #8a95a8;
  line-height: 1.5;
}

.btn-back {
  display: inline-block;
  margin-top: 2.5rem;
  padding: 0.7rem 2rem;
  border-radius: 999px;
  border: 1px solid rgba(134, 232, 255, 0.3);
