import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { SplitText } from 'gsap/SplitText';
import Lenis from 'lenis';

// ── Plugin registration ────────────────────────────────────────
gsap.registerPlugin(ScrollTrigger, SplitText);

// ── Demo Shell ─────────────────────────────────────────────────
initDemoShell({
  title: 'UX/UI Designer Portfolio',
  category: 'pages',
  tech: ['gsap', 'lenis', 'scrolltrigger', 'splittext', 'view-transitions-api']
});

// ── Reduced motion ─────────────────────────────────────────────
let reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

window.addEventListener('motion-preference', (e) => {
  reduced = e.detail.reduced;
  document.documentElement.classList.toggle('reduced-motion', reduced);
  ScrollTrigger.refresh();
});

const dur = (d) => reduced ? 0 : d;

// ── Lenis smooth scroll ────────────────────────────────────────
const lenis = new Lenis({ lerp: 0.1, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

// ═══════════════════════════════════════════════════════════════
// HERO ANIMATIONS
// ═══════════════════════════════════════════════════════════════

function initHero() {
  const nameEl = document.querySelector('.hero__name');
  if (!nameEl) return;

  // SplitText — char by char animation
  const split = new SplitText(nameEl, { type: 'chars', charsClass: 'char' });

  const tl = gsap.timeline({ defaults: { ease: 'power3.out' } });

  tl.from(split.chars, {
    y: 60,
    opacity: 0,
    duration: dur(1),
    stagger: 0.03,
  })
  .to('.hero__eyebrow', {
    opacity: 1,
    duration: dur(0.6),
  }, '-=0.5')
  .to('.hero__role', {
    opacity: 1,
    y: 0,
    duration: dur(0.5),
  }, '-=0.3')
  .to('.hero__tagline', {
    opacity: 1,
    duration: dur(0.5),
  }, '-=0.2')
  .to('.hero__cta', {
    opacity: 1,
    duration: dur(0.4),
  }, '-=0.1')
  .to('.hero__scroll-hint', {
    opacity: 1,
    duration: dur(0.4),
  }, '-=0.1');

  // Parallax on background deco circles
  if (!reduced) {
    gsap.to('.deco-circle--1', {
      y: -80,
      ease: 'none',
      scrollTrigger: {
        trigger: '.hero',
        start: 'top top',
        end: 'bottom top',
        scrub: 1.5,
      },
    });

    gsap.to('.deco-circle--2', {
      y: -40,
      ease: 'none',
      scrollTrigger: {
        trigger: '.hero',
        start: 'top top',
        end: 'bottom top',
        scrub: 2,
      },
    });
  }
}

// ═══════════════════════════════════════════════════════════════
// ROTATING WORDS
// ═══════════════════════════════════════════════════════════════

function initRotatingWords() {
  const wordEl = document.getElementById('rotating-word');
  if (!wordEl) return;

  const words = ['product', 'interaction', 'visual', 'systems'];
  let currentIndex = 0;

  function rotateWord() {
    currentIndex = (currentIndex + 1) % words.length;

    if (reduced) {
      wordEl.textContent = words[currentIndex];
      return;
    }

    gsap.timeline()
      .to(wordEl, {
        opacity: 0,
        y: -10,
        duration: 0.3,
        ease: 'power2.in',
        onComplete: () => {
          wordEl.textContent = words[currentIndex];
          gsap.set(wordEl, { y: 12 });
        },
      })
      .to(wordEl, {
        opacity: 1,
        y: 0,
        duration: 0.4,
        ease: 'power3.out',
      });
  }

  // Start rotating after hero animation finishes
  setTimeout(() => {
    setInterval(rotateWord, 2500);
  }, reduced ? 0 : 2000);
}

// ═══════════════════════════════════════════════════════════════
// SECTION HEADING REVEALS
// ═══════════════════════════════════════════════════════════════

function initHeadingReveals() {
  const headings = document.querySelectorAll('.reveal-heading');

  headings.forEach((heading) => {
    gsap.to(heading, {
      opacity: 1,
      y: 0,
      duration: dur(0.8),
      ease: 'power3.out',
      scrollTrigger: {
        trigger: heading,
        start: 'top 85%',
        toggleActions: 'play none none none',
      },
    });
  });
}

// ═══════════════════════════════════════════════════════════════
// WORK CARDS
// ═══════════════════════════════════════════════════════════════

function initWorkCards() {
  const cards = document.querySelectorAll('.case-card');

  cards.forEach((card, i) => {
    gsap.to(card, {
      opacity: 1,
      y: 0,
      duration: dur(0.7),
      ease: 'power3.out',
      delay: reduced ? 0 : (i % 2) * 0.12,
      scrollTrigger: {
        trigger: card,
        start: 'top 88%',
        toggleActions: 'play none none none',
      },
    });
  });
}

