// ═══════════════════════════════════════════════════════════════

function initProcessSteps() {
  const steps = document.querySelectorAll('.process-step');
  if (!steps.length) return;

  gsap.to(steps, {
    opacity: 1,
    y: 0,
    duration: dur(0.7),
    stagger: reduced ? 0 : 0.12,
    ease: 'power3.out',
    scrollTrigger: {
      trigger: '.process-row',
      start: 'top 80%',
      toggleActions: 'play none none none',
    },
  });
}

