// ABOUT SECTION
// ═══════════════════════════════════════════════════════════════

function initAbout() {
  const image = document.querySelector('.about-image');
  const content = document.querySelector('.about-content');

  if (image) {
    gsap.to(image, {
      opacity: 1,
      x: 0,
      duration: dur(0.9),
      ease: 'power3.out',
      scrollTrigger: {
        trigger: '.about-layout',
        start: 'top 80%',
        toggleActions: 'play none none none',
      },
    });
  }

  if (content) {
    gsap.to(content, {
      opacity: 1,
      x: 0,
      duration: dur(0.9),
      ease: 'power3.out',
      delay: reduced ? 0 : 0.15,
      scrollTrigger: {
        trigger: '.about-layout',
        start: 'top 80%',
        toggleActions: 'play none none none',
      },
    });
  }

  // Skill pills stagger
  const pills = document.querySelectorAll('.skill-pill');
  gsap.from(pills, {
    opacity: 0,
    scale: 0.85,
    duration: dur(0.4),
    stagger: reduced ? 0 : 0.04,
    ease: 'back.out(1.5)',
    scrollTrigger: {
      trigger: '.about-skills',
      start: 'top 85%',
      toggleActions: 'play none none none',
    },
  });
}

