// FOOTER ANIMATION
// ═══════════════════════════════════════════════════════════════

function initFooter() {
  const headline = document.querySelector('.footer__headline');
  if (!headline) return;

  gsap.to(headline, {
    opacity: 1,
    y: 0,
    duration: dur(1),
    ease: 'power3.out',
    scrollTrigger: {
      trigger: '.footer',
      start: 'top 85%',
      toggleActions: 'play none none none',
    },
  });
}

