  min-height: 100vh;
}

.view.active {
  display: block;
}

/* ── Page header ── */
.page-header {
  text-align: center;
  padding: 5rem 2rem 2rem;
}

.eyebrow {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.18em;
