.layer {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  will-change: transform;
}

.layer-fg {
  position: relative;
  z-index: 3;
}

.layer-mid { z-index: 2; }
.layer-bg { z-index: 1; }

