/* ─── Reset & Base ─────────────────────────────────────────────────────── */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg:       #ffffff;
  --dark:     #0d1b2a;
  --text:     #0d1b2a;
  --muted:    #6b7a8d;
  --accent:   #2563eb;
  --light:    #f0f4ff;
  --border:   #e2e8f0;
  --radius:   12px;
  --radius-sm: 6px;
  --font:     -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Inter', 'Helvetica Neue', Arial, sans-serif;
  --font-mono: 'SFMono-Regular', 'Consolas', 'Liberation Mono', 'Menlo', monospace;
}

html { scroll-behavior: smooth; }

body {
  font-family: var(--font);
  background: var(--bg);
  color: var(--text);
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

a { color: inherit; text-decoration: none; }
ul { list-style: none; }
img { display: block; max-width: 100%; }

/* ─── Shared Section Layout ────────────────────────────────────────────── */
.section {
  padding: clamp(5rem, 10vw, 9rem) clamp(1.5rem, 6vw, 7rem);
  max-width: 1240px;
  margin: 0 auto;
}

.section__header {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: 2rem;
  margin-bottom: clamp(3rem, 6vw, 5rem);
  padding-bottom: 2rem;
  border-bottom: 1px solid var(--border);
}

.section__label-group {
  display: flex;
  align-items: baseline;
  gap: 1rem;
}

.section-index {
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--accent);
  font-family: var(--font-mono);
  letter-spacing: 0.05em;
}

.section-label {
  font-size: 0.8rem;
  font-weight: 700;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.section__summary {
  font-size: 0.9rem;
  color: var(--muted);
  max-width: 30ch;
  line-height: 1.6;
  text-align: right;
}

/* ─── Hero ──────────────────────────────────────────────────────────────── */
.hero {
  position: relative;
  min-height: 100svh;
  background: var(--dark);
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.hero__canvas {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;
}

.hero__inner {
  position: relative;
  z-index: 1;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  flex: 1;
  padding: clamp(2rem, 4vw, 4rem) clamp(1.5rem, 6vw, 7rem);
  padding-bottom: 5rem;
}

.hero__eyebrow {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-top: 1rem;
}

.eyebrow-tag {
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: rgba(255,255,255,0.5);
}

.eyebrow-dot {
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background: var(--accent);
  flex-shrink: 0;
}

.eyebrow-avail {
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--accent);
  letter-spacing: 0.04em;
}

.hero__name {
  font-size: clamp(4rem, 12vw, 10rem);
  font-weight: 900;
  line-height: 0.9;
  letter-spacing: -0.04em;
  color: #ffffff;
  margin-top: auto;
  padding-top: 2rem;
  overflow: hidden;
}

.hero__name .line {
  overflow: hidden;
  display: block;
}

.hero__foot {
  display: flex;
  align-items: flex-end;
  justify-content: space-between;
  gap: 2rem;
  flex-wrap: wrap;
  margin-top: 3rem;
}

.hero__tagline {
  font-size: clamp(1rem, 2vw, 1.2rem);
  font-weight: 400;
  line-height: 1.6;
  color: rgba(255,255,255,0.55);
  max-width: 36ch;
}

.hero__ctas {
  display: flex;
  gap: 0.875rem;
  flex-shrink: 0;
}

.btn {
  display: inline-flex;
  align-items: center;
  height: 48px;
  padding: 0 1.75rem;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 600;
  letter-spacing: 0.02em;
  transition: transform 0.2s ease, opacity 0.2s ease, background 0.2s ease;
  cursor: pointer;
}

.btn--primary {
  background: var(--accent);
  color: #fff;
}

.btn--primary:hover {
  background: #1d4fd8;
  transform: translateY(-1px);
}

.btn--ghost {
  background: transparent;
  color: rgba(255,255,255,0.7);
  border: 1px solid rgba(255,255,255,0.2);
}

.btn--ghost:hover {
  background: rgba(255,255,255,0.06);
  color: #fff;
  border-color: rgba(255,255,255,0.35);
}

.hero__scroll-indicator {
  position: absolute;
  bottom: 2.5rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  z-index: 2;
}

.scroll-track {
  width: 1px;
  height: 48px;
  background: rgba(255,255,255,0.12);
  border-radius: 1px;
  overflow: hidden;
  position: relative;
}

.scroll-thumb {
  position: absolute;
  top: -100%;
  left: 0;
  width: 100%;
  height: 50%;
  background: var(--accent);
  border-radius: 1px;
  animation: scrollThumb 1.8s ease-in-out infinite;
}

@keyframes scrollThumb {
  0%   { top: -50%; }
  100% { top: 100%; }
}

.scroll-label {
  font-size: 0.65rem;
  font-weight: 600;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: rgba(255,255,255,0.3);
}

/* ─── Expertise Section ─────────────────────────────────────────────────── */
.expertise {
  background: var(--bg);
}

.expertise__grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
}

@media (max-width: 900px) {
  .expertise__grid { grid-template-columns: 1fr; }
}

.expertise-card {
  background: var(--light);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 2.5rem 2rem;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.expertise-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 16px 48px rgba(37,99,235,0.08);
}

/* CSS geometric icons */
.expertise-card__icon {
  width: 48px;
  height: 48px;
  margin-bottom: 1.5rem;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.icon-backend {
  width: 36px;
  height: 36px;
  border: 2.5px solid var(--accent);
  border-radius: 6px;
  position: relative;
}

.icon-backend::before {
  content: '';
  position: absolute;
  top: 6px;
  left: 6px;
  right: 6px;
  height: 2px;
  background: var(--accent);
  border-radius: 1px;
  box-shadow: 0 6px 0 var(--accent), 0 12px 0 var(--accent);
}

.icon-platform {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  border: 2.5px solid var(--accent);
  position: relative;
}

.icon-platform::before {
  content: '';
  position: absolute;
  inset: 6px;
  border-radius: 50%;
  border: 2.5px solid var(--accent);
}

.icon-platform::after {
  content: '';
  position: absolute;
  width: 8px;
  height: 8px;
  background: var(--accent);
  border-radius: 50%;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.icon-leadership {
  width: 36px;
  height: 36px;
  position: relative;
}

.icon-leadership::before {
  content: '';
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 28px;
  height: 18px;
  border: 2.5px solid var(--accent);
  border-radius: 14px 14px 6px 6px;
  border-bottom: none;
}

.icon-leadership::after {
  content: '';
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 14px;
  height: 14px;
  border: 2.5px solid var(--accent);
  border-radius: 50%;
}

.expertise-card__title {
  font-size: 1.1rem;
  font-weight: 700;
  letter-spacing: -0.02em;
  margin-bottom: 1.25rem;
  color: var(--text);
}

.expertise-card__list {
  display: flex;
  flex-direction: column;
  gap: 0.6rem;
}

.expertise-card__list li {
  font-size: 0.875rem;
  color: var(--muted);
  line-height: 1.5;
  padding-left: 1rem;
  position: relative;
}

.expertise-card__list li::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0.55em;
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background: var(--accent);
}

/* ─── Timeline Section ──────────────────────────────────────────────────── */
.timeline {
  background: var(--bg);
  padding-top: 0;
}

.timeline__track {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 0;
}

