/* ─── Reduced Motion ────────────────────────────────────────────────────── */
.reduced-motion *,
.reduced-motion *::before,
.reduced-motion *::after {
  animation-duration: 0.01ms !important;
  transition-duration: 0.01ms !important;
}

.reduced-motion .scroll-thumb {
  animation: none !important;
}

