/* ═══════════════════════════════════════════════════════════════════════
   AI ENGINEER PORTFOLIO — styles.css
   Dr. Yuki Tanaka // Futuristic data-dense monospace aesthetic
   ═══════════════════════════════════════════════════════════════════════ */

/* ── Reset & Base ───────────────────────────────────────────────────── */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg:            #050508;
  --text:          #e0ffe8;
  --dim:           #1a2435;
  --accent-green:  #00ff88;
  --accent-purple: #9945ff;
  --accent-blue:   #00d4ff;
  --muted:         #3a5a4a;
  --border:        #0f2020;
  --mono:          'JetBrains Mono', 'Fira Code', 'Cascadia Code', 'Courier New', monospace;
}

html { scroll-behavior: smooth; }

body {
  background: var(--bg);
  color: var(--text);
  font-family: var(--mono);
  font-size: 14px;
  line-height: 1.6;
  overflow-x: hidden;
}

a { color: var(--accent-green); text-decoration: none; }
a:hover { text-decoration: underline; }

/* ── Section base ───────────────────────────────────────────────────── */
.section {
  padding: 100px clamp(24px, 6vw, 120px);
  position: relative;
  max-width: 1400px;
  margin: 0 auto;
}

.section-header {
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 60px;
}

.section-tag {
  font-size: 11px;
  color: var(--accent-green);
  letter-spacing: 0.2em;
  opacity: 0.7;
  white-space: nowrap;
}

.section-title {
  font-size: clamp(16px, 2vw, 22px);
  font-weight: 900;
  letter-spacing: 0.18em;
  color: var(--text);
  white-space: nowrap;
  text-transform: uppercase;
}

.section-line {
  flex: 1;
  height: 1px;
  background: linear-gradient(90deg, var(--muted) 0%, transparent 100%);
}

/* ── HERO ───────────────────────────────────────────────────────────── */
#hero {
  position: relative;
  width: 100%;
  height: 100vh;
  min-height: 600px;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

#neural-canvas {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
}

#hero-overlay {
  position: relative;
  z-index: 2;
  text-align: center;
  padding: 0 clamp(16px, 5vw, 80px);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
}

#hero-meta {
  font-size: 10px;
  letter-spacing: 0.35em;
  color: var(--accent-green);
  opacity: 0.6;
  text-transform: uppercase;
}

#hero-name {
  font-size: clamp(3rem, 10vw, 9rem);
  font-weight: 900;
  letter-spacing: 0.06em;
  color: var(--text);
  text-transform: uppercase;
  line-height: 0.9;
  text-shadow:
    0 0 30px rgba(0,255,136,0.3),
    0 0 80px rgba(0,255,136,0.1);
  will-change: transform;
}

#hero-title {
  font-size: clamp(12px, 1.8vw, 18px);
  letter-spacing: 0.3em;
  color: var(--accent-blue);
  text-transform: uppercase;
  opacity: 0.9;
}

#hero-spec-wrapper {
  display: flex;
  align-items: center;
  gap: 0;
  font-size: clamp(12px, 1.5vw, 16px);
  color: var(--accent-green);
  height: 28px;
  letter-spacing: 0.05em;
  background: rgba(0,255,136,0.05);
  border: 1px solid rgba(0,255,136,0.15);
  padding: 4px 16px;
  border-radius: 2px;
}

#spec-label { opacity: 0.6; }

#spec-cursor {
  animation: blink 0.9s step-end infinite;
  color: var(--accent-green);
  margin-left: 2px;
}

@keyframes blink { 0%,100%{opacity:1} 50%{opacity:0} }

#hero-stats {
  display: flex;
  align-items: center;
  gap: 20px;
  margin-top: 20px;
  flex-wrap: wrap;
  justify-content: center;
}

.stat-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
}

.stat-val {
  font-size: clamp(20px, 3vw, 32px);
  font-weight: 900;
  color: var(--accent-green);
  line-height: 1;
  text-shadow: 0 0 20px rgba(0,255,136,0.5);
}

.stat-label {
  font-size: 9px;
  letter-spacing: 0.2em;
  color: var(--muted);
  text-transform: uppercase;
}

.stat-sep {
  color: var(--border);
  font-size: 24px;
  opacity: 0.5;
  align-self: center;
  margin-bottom: 12px;
}

#hero-scroll-hint {
  position: absolute;
  bottom: 28px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 9px;
  letter-spacing: 0.3em;
  color: var(--muted);
  z-index: 2;
  animation: float-hint 2s ease-in-out infinite;
