//  CANVAS 2D — Training Loss Curve
// ══════════════════════════════════════════════════════════════════════
function initLossCanvas() {
  const canvas = document.getElementById('loss-canvas');
  const ctx = canvas.getContext('2d');

  // Generate a realistic loss curve: starts ~2.5, drops fast, levels off ~0.15
  function generateLossData(numPoints) {
    const data = [];
    let loss = 2.5;
    for (let i = 0; i < numPoints; i++) {
      const t = i / numPoints;
      const decay = Math.exp(-t * 5) * 2.35;
      const plateau = 0.15;
      const noise = (Math.random() - 0.5) * 0.08 * Math.exp(-t * 3);
      loss = plateau + decay + noise;
      data.push(Math.max(0.05, loss));
    }
    return data;
  }

  const DATA_POINTS = 120;
  const lossData = generateLossData(DATA_POINTS);

  function resizeCanvas() {
    const dpr = window.devicePixelRatio || 1;
    const rect = canvas.getBoundingClientRect();
    canvas.width  = rect.width  * dpr;
    canvas.height = rect.height * dpr;
    ctx.scale(dpr, dpr);
    return { w: rect.width, h: rect.height };
  }

  function drawChart(progress) {
    const { w, h } = resizeCanvas();
    const PAD = { top: 20, right: 20, bottom: 30, left: 48 };
    const plotW = w - PAD.left - PAD.right;
    const plotH = h - PAD.top - PAD.bottom;

    ctx.clearRect(0, 0, w, h);

    // Background
    ctx.fillStyle = '#020508';
    ctx.fillRect(0, 0, w, h);

    // Grid lines
    const gridCols = 8;
    const gridRows = 5;
    ctx.strokeStyle = 'rgba(15,32,32,1)';
    ctx.lineWidth = 1;

    for (let i = 0; i <= gridCols; i++) {
      const x = PAD.left + (i / gridCols) * plotW;
      ctx.beginPath();
      ctx.moveTo(x, PAD.top);
      ctx.lineTo(x, PAD.top + plotH);
      ctx.stroke();
    }
    for (let i = 0; i <= gridRows; i++) {
      const y = PAD.top + (i / gridRows) * plotH;
      ctx.beginPath();
      ctx.moveTo(PAD.left, y);
      ctx.lineTo(PAD.left + plotW, y);
      ctx.stroke();
    }

    // Y axis labels
    ctx.fillStyle = '#3a5a4a';
    ctx.font = '9px JetBrains Mono, monospace';
    ctx.textAlign = 'right';
    const yMax = 2.6;
    const yMin = 0.0;
    for (let i = 0; i <= gridRows; i++) {
      const val = yMax - (i / gridRows) * (yMax - yMin);
      const y = PAD.top + (i / gridRows) * plotH;
      ctx.fillText(val.toFixed(1), PAD.left - 6, y + 3);
    }

    // X axis labels
    ctx.textAlign = 'center';
    for (let i = 0; i <= gridCols; i++) {
      const val = Math.round((i / gridCols) * 100);
      const x = PAD.left + (i / gridCols) * plotW;
      ctx.fillText(val + 'k', x, PAD.top + plotH + 16);
    }

    // Axes
    ctx.strokeStyle = '#1a2435';
    ctx.lineWidth = 1.5;
    ctx.beginPath();
    ctx.moveTo(PAD.left, PAD.top);
    ctx.lineTo(PAD.left, PAD.top + plotH);
    ctx.lineTo(PAD.left + plotW, PAD.top + plotH);
    ctx.stroke();

    // Clip to progress
    const drawCount = Math.floor(DATA_POINTS * progress);
    if (drawCount < 2) return;

    function dataToCanvas(i, val) {
      const x = PAD.left + (i / (DATA_POINTS - 1)) * plotW;
      const y = PAD.top + (1 - (val - yMin) / (yMax - yMin)) * plotH;
      return { x, y };
    }

    // Glow fill under curve
    const firstPt = dataToCanvas(0, lossData[0]);
    const lastPt  = dataToCanvas(drawCount - 1, lossData[drawCount - 1]);

    const gradient = ctx.createLinearGradient(0, PAD.top, 0, PAD.top + plotH);
    gradient.addColorStop(0, 'rgba(0,255,136,0.18)');
    gradient.addColorStop(1, 'rgba(0,255,136,0.00)');

    ctx.beginPath();
    ctx.moveTo(firstPt.x, PAD.top + plotH);
    ctx.lineTo(firstPt.x, firstPt.y);
    for (let i = 1; i < drawCount; i++) {
      const pt = dataToCanvas(i, lossData[i]);
      ctx.lineTo(pt.x, pt.y);
    }
    ctx.lineTo(lastPt.x, PAD.top + plotH);
    ctx.closePath();
    ctx.fillStyle = gradient;
    ctx.fill();

    // Glowing line
    ctx.shadowColor = '#00ff88';
    ctx.shadowBlur = 12;
    ctx.strokeStyle = '#00ff88';
    ctx.lineWidth = 2;
    ctx.lineJoin = 'round';

    ctx.beginPath();
    const startPt = dataToCanvas(0, lossData[0]);
    ctx.moveTo(startPt.x, startPt.y);
    for (let i = 1; i < drawCount; i++) {
      const pt = dataToCanvas(i, lossData[i]);
      ctx.lineTo(pt.x, pt.y);
    }
    ctx.stroke();
    ctx.shadowBlur = 0;

    // Leading dot
    const leadPt = dataToCanvas(drawCount - 1, lossData[drawCount - 1]);
    ctx.beginPath();
    ctx.arc(leadPt.x, leadPt.y, 4, 0, Math.PI * 2);
    ctx.fillStyle = '#00ff88';
    ctx.shadowColor = '#00ff88';
    ctx.shadowBlur = 16;
    ctx.fill();
    ctx.shadowBlur = 0;
  }

  // Initial draw at 0
  drawChart(0);

  if (reduced) {
    drawChart(1);
    return;
  }

  // Animate on scroll entry
  let animated = false;
  ScrollTrigger.create({
    trigger: '#loss-panel',
    start: 'top 80%',
    onEnter: () => {
      if (animated) return;
      animated = true;
      const obj = { progress: 0 };
      gsap.to(obj, {
        progress: 1,
        duration: 2.2,
        ease: 'power2.inOut',
        onUpdate: () => drawChart(obj.progress)
      });
    }
  });

  window.addEventListener('resize', () => drawChart(animated ? 1 : 0));
}

// ══════════════════════════════════════════════════════════════════════
//  CANVAS 2D — Confusion Matrix (8×8 heatmap)
// ══════════════════════════════════════════════════════════════════════
function initConfusionMatrix() {
  const container = document.getElementById('confusion-matrix');

  // Simulate a realistic 8x8 confusion matrix (high diagonal, some off-diagonal)
  const labels = ['NLI', 'QA', 'SUM', 'CLS', 'GEN', 'COD', 'MAT', 'RLX'];
  const N = 8;

  // Build matrix: diagonal values high, off-diagonal noise
  const matrix = [];
  for (let i = 0; i < N; i++) {
    const row = [];
    for (let j = 0; j < N; j++) {
      if (i === j) {
        row.push(0.82 + Math.random() * 0.17);    // diagonal: 0.82–0.99
      } else {
        const off = Math.random() * 0.12;
        row.push(off * (Math.random() > 0.65 ? 1 : 0.3)); // sparse off-diagonal
      }
    }
    matrix.push(row);
  }

  // Create cells
  for (let i = 0; i < N; i++) {
    for (let j = 0; j < N; j++) {
      const val = matrix[i][j];
      const cell = document.createElement('div');
      cell.className = 'cm-cell';

      // Color: green for diagonal, purple for off-diagonal confusion, dark for zero
      let r, g, b, a;
      if (i === j) {
        // Green diagonal
        const intensity = val;
        r = Math.round(0   + intensity * 0);
        g = Math.round(100 + intensity * 155);
        b = Math.round(60  + intensity * 76);
        a = 0.3 + intensity * 0.7;
      } else if (val > 0.04) {
        // Purple off-diagonal errors
        const intensity = val / 0.12;
        r = Math.round(80 + intensity * 73);
        g = Math.round(10 + intensity * 20);
        b = Math.round(120 + intensity * 135);
        a = 0.2 + intensity * 0.7;
      } else {
        r = 10; g = 20; b = 30; a = 0.4;
      }

      cell.style.background = `rgba(${r},${g},${b},${a})`;

      if (val > 0.05) {
        cell.textContent = val.toFixed(2);
      }

      // Stagger reveal
      cell.style.opacity = '0';
      cell.style.transform = 'scale(0.5)';
      container.appendChild(cell);
    }
  }

  if (reduced) {
    container.querySelectorAll('.cm-cell').forEach(c => {
      c.style.opacity = '1';
      c.style.transform = 'none';
    });
    return;
  }

  ScrollTrigger.create({
    trigger: '#confusion-panel',
    start: 'top 80%',
    onEnter: () => {
      const cells = container.querySelectorAll('.cm-cell');
      cells.forEach((cell, i) => {
        gsap.to(cell, {
          opacity: 1,
          scale: 1,
          duration: 0.35,
          delay: i * 0.008,
          ease: 'back.out(1.4)'
        });
      });
    }
  });
}

// ══════════════════════════════════════════════════════════════════════
//  TERMINAL STACK — stagger reveal
// ══════════════════════════════════════════════════════════════════════
function initTerminal() {
  const lines = document.querySelectorAll('.term-line, .term-output');

  if (reduced) {
    lines.forEach(l => { l.style.opacity = '1'; l.style.transform = 'none'; });
    document.querySelectorAll('.term-bar-fill').forEach(b => { b.style.width = b.style.getPropertyValue('--pct'); });
    return;
  }

  ScrollTrigger.create({
    trigger: '#terminal-body',
    start: 'top 82%',
    onEnter: () => {
      lines.forEach((line, i) => {
        const delay = i * 0.1;
        gsap.to(line, {
          opacity: 1,
          x: 0,
          duration: 0.4,
          delay,
          ease: 'power2.out'
        });

        // Animate skill bars
        const bar = line.querySelector('.term-bar-fill');
        if (bar) {
          const targetPct = bar.style.getPropertyValue('--pct');
          gsap.to(bar, {
            width: targetPct,
            duration: 1.0,
            delay: delay + 0.15,
            ease: 'power2.out'
          });
        }
      });
    }
  });
}

// ══════════════════════════════════════════════════════════════════════
//  CONTACT — fade in
// ══════════════════════════════════════════════════════════════════════
function initContact() {
  if (reduced) return;

  gsap.fromTo('.contact-terminal',
    { opacity: 0, y: 30 },
    {
      opacity: 1, y: 0,
      duration: 0.9,
      ease: 'power3.out',
      scrollTrigger: {
        trigger: '#contact',
        start: 'top 85%',
        once: true
      }
    }
  );

  gsap.fromTo('.contact-row',
    { opacity: 0, x: -20 },
    {
      opacity: 1, x: 0,
      duration: 0.5,
      stagger: 0.1,
      ease: 'power2.out',
      scrollTrigger: {
        trigger: '.contact-output',
        start: 'top 88%',
        once: true
      }
    }
  );
}

// ══════════════════════════════════════════════════════════════════════
//  SECTION HEADERS — reveal
// ══════════════════════════════════════════════════════════════════════
function initSectionHeaders() {
  if (reduced) return;

  document.querySelectorAll('.section-header').forEach(header => {
    gsap.fromTo(header,
      { opacity: 0, y: 20 },
      {
        opacity: 1, y: 0,
        duration: 0.7,
        ease: 'power2.out',
        scrollTrigger: {
          trigger: header,
          start: 'top 90%',
          once: true
        }
      }
    );
  });
}

// ══════════════════════════════════════════════════════════════════════
//  REDUCED MOTION LISTENER
// ══════════════════════════════════════════════════════════════════════
window.addEventListener('motion-preference', (e) => {
  // If user toggles motion mid-session, refresh page to reinitialize
  // (complex animation state makes incremental change impractical)
  location.reload();
});

// ══════════════════════════════════════════════════════════════════════
//  BOOT
// ══════════════════════════════════════════════════════════════════════
initNeuralNet();
initHeroText();
initPaperCards();
initLossCanvas();
initConfusionMatrix();
initTerminal();
initContact();
initSectionHeaders();

snippets.css:
:root {
  --page-bg: #0a0a0f;
  --page-surface: #141418;
  --page-text: #f5f5f7;
  --page-muted: #86868b;
  --page-accent: #2997ff;
  --page-accent-alt: #bf5af2;
  --page-gold: #ffd60a;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

body {
  background: var(--page-bg);
  color: var(--page-text);
  font-family: 'SF Pro Display', 'Inter', system-ui, -apple-system, sans-serif;
  line-height: 1.6;
  overflow-x: hidden;
}

/* ── Three.js canvas ── */
#canvas-container {
  position: fixed;
  inset: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  pointer-events: none;
}

#canvas-container canvas {
  width: 100%;
  height: 100%;
}

/* ── Sections ── */
.section {
  position: relative;
  z-index: 1;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 4rem 2rem;
}

/* ── Hero ── */
.hero-section {
  text-align: center;
}

.hero-content {
  position: relative;
  z-index: 2;
}

.hero-title {
  font-size: clamp(3rem, 10vw, 8rem);
  font-weight: 700;
  letter-spacing: -0.03em;
  line-height: 1;
  margin-bottom: 0.5rem;
  perspective: 800px;
}

.hero-tagline {
  font-size: clamp(1rem, 3vw, 1.5rem);
  color: var(--page-muted);
  font-weight: 400;
  letter-spacing: 0.02em;
  opacity: 0;
}

.scroll-indicator {
  margin-top: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  color: var(--page-muted);
  font-size: 0.7rem;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  opacity: 0;
}

.scroll-indicator svg {
  animation: scroll-bounce 2s ease-in-out infinite;
}

@keyframes scroll-bounce {
  0%, 100% { transform: translateY(0); opacity: 1; }
  50% { transform: translateY(6px); opacity: 0.4; }
}

.reduced-motion .scroll-indicator svg { animation: none; }

/* ── Feature Track (pinned) ── */
.feature-track {
  position: relative;
  z-index: 1;
  height: 300vh;
}

.feature-container {
  position: sticky;
  top: 0;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding-right: 8%;
}

.feature-content {
  max-width: 420px;
  position: relative;
}

/* Step dots */
.step-dots {
  display: flex;
  gap: 0.5rem;
  margin-bottom: 2rem;
}

.step-dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  transition: background 0.4s ease, transform 0.4s ease;
}

.step-dot.active {
  background: var(--page-accent);
  transform: scale(1.3);
}

/* Feature cards */
.feature-card {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  opacity: 0;
  transform: translateY(30px);
  pointer-events: none;
  transition: opacity 0.5s ease, transform 0.5s ease;
}

.feature-card.active {
  opacity: 1;
  transform: translateY(0);
  pointer-events: auto;
  position: relative;
}

.reduced-motion .feature-card {
  transition: none;
}

.feature-icon {
  width: 48px;
  height: 48px;
  color: var(--page-accent);
  margin-bottom: 1.5rem;
}

.feature-label {
  font-size: 0.7rem;
  color: var(--page-accent);
  text-transform: uppercase;
  letter-spacing: 0.15em;
  font-weight: 600;
}

.feature-heading {
  font-size: clamp(1.5rem, 4vw, 2.5rem);
  font-weight: 700;
  letter-spacing: -0.02em;
  margin: 0.5rem 0 1rem;
  line-height: 1.15;
}

.feature-desc {
  font-size: 0.95rem;
  color: var(--page-muted);
  line-height: 1.7;
  margin-bottom: 1.5rem;
}

.feature-stat {
  display: flex;
  align-items: baseline;
  gap: 0.5rem;
}

.stat-value {
  font-size: 3rem;
  font-weight: 700;
  color: var(--page-accent);
  line-height: 1;
  font-variant-numeric: tabular-nums;
}

.stat-unit {
  font-size: 0.8rem;
  color: var(--page-muted);
  text-transform: uppercase;
  letter-spacing: 0.04em;
}

/* ── Specs Grid ── */
.specs-section {
  padding: 8rem 2rem;
}

.specs-heading {
  font-size: clamp(1.8rem, 5vw, 3rem);
  font-weight: 700;
  letter-spacing: -0.02em;
  margin-bottom: 4rem;
  text-align: center;
}

.specs-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
  max-width: 800px;
  width: 100%;
}

.spec-card {
  background: var(--page-surface);
  border: 1px solid rgba(255, 255, 255, 0.06);
  border-radius: 16px;
  padding: 2rem 1.5rem;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.75rem;
  opacity: 0;
  transform: translateY(30px) scale(0.95);
  transition: border-color 0.3s ease, box-shadow 0.3s ease;
}

.spec-card:hover {
  border-color: rgba(41, 151, 255, 0.2);
  box-shadow: 0 8px 32px rgba(41, 151, 255, 0.08);
}

.reduced-motion .spec-card {
  opacity: 1;
  transform: none;
}

.spec-icon {
  width: 24px;
  height: 24px;
  color: var(--page-accent);
}

.spec-value {
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--page-text);
}

.spec-label {
  font-size: 0.7rem;
  color: var(--page-muted);
  text-transform: uppercase;
  letter-spacing: 0.06em;
}

/* ── CTA Section ── */
.cta-section {
  position: relative;
  overflow: hidden;
}

.cta-content {
  position: relative;
  z-index: 2;
  text-align: center;
}

.cta-price {
  font-size: clamp(3rem, 10vw, 6rem);
  font-weight: 700;
  letter-spacing: -0.02em;
  line-height: 1;
  margin-bottom: 1rem;
  color: var(--page-text);
}

.cta-tagline {
  font-size: clamp(0.9rem, 2vw, 1.15rem);
  color: var(--page-muted);
  margin-bottom: 3rem;
  opacity: 0;
}

.cta-buttons {
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
}

.btn {
  padding: 0.9rem 2.5rem;
  border-radius: 999px;
  font: 600 0.9rem/1 'SF Pro Display', 'Inter', system-ui, sans-serif;
  cursor: pointer;
  transition: all 0.3s ease;
  border: none;
  opacity: 0;
  transform: translateY(20px);
}

.btn-primary {
  background: var(--page-gold);
  color: #0a0a0f;
}

.btn-primary:hover {
  background: #ffe040;
  box-shadow: 0 0 24px rgba(255, 214, 10, 0.3);
}

.btn-secondary {
  background: transparent;
  border: 1.5px solid rgba(255, 255, 255, 0.2);
  color: var(--page-accent);
}

.btn-secondary:hover {
  border-color: var(--page-accent);
  box-shadow: 0 0 16px rgba(41, 151, 255, 0.15);
}

.reduced-motion .btn,
.reduced-motion .cta-tagline {
  opacity: 1;
  transform: none;
}

/* Gradient orbs */
.orb {
  position: absolute;
  border-radius: 50%;
  filter: blur(100px);
  pointer-events: none;
  z-index: 0;
}

.orb-1 {
  width: 500px;
  height: 500px;
  background: rgba(41, 151, 255, 0.12);
  bottom: -100px;
  left: -100px;
}

.orb-2 {
  width: 400px;
  height: 400px;
  background: rgba(191, 90, 242, 0.1);
  top: -50px;
  right: -80px;
}

/* ── Responsive ── */
@media (max-width: 768px) {
  .feature-container {
    justify-content: center;
    padding: 0 2rem;
  }

  .specs-grid {
    grid-template-columns: repeat(2, 1fr);
  }

  .section {
    padding: 4rem 1.25rem;
  }
}

@media (max-width: 480px) {
  .specs-grid {
    grid-template-columns: 1fr 1fr;
    gap: 1rem;
  }

  .spec-card {
    padding: 1.5rem 1rem;
  }
}

