:root {
  --bg: #07111d;
  --panel: rgba(18, 31, 49, 0.72);
  --line: #2a4462;
  --text: #edf5ff;
  --muted: #bcd0e8;
  --accent: #79e5ff;
}

* { box-sizing: border-box; }

body {
  margin: 0;
  color: var(--text);
  font-family: "Lucida Console", "Monaco", monospace;
  background:
    radial-gradient(circle at 12% 10%, rgba(121, 229, 255, 0.18), transparent 42%),
    radial-gradient(circle at 84% 86%, rgba(91, 123, 255, 0.2), transparent 46%),
    var(--bg);
}

.topbar {
  position: fixed;
  inset: 0 0 auto 0;
  z-index: 20;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.8rem 1rem;
  background: rgba(7, 17, 29, 0.66);
  backdrop-filter: blur(8px);
}

.topbar a {
  color: var(--accent);
  text-decoration: none;
  font-weight: 700;
}

.toggle {
  border: 1px solid rgba(255,255,255,0.25);
  border-radius: 999px;
  background: rgba(255,255,255,0.08);
  color: var(--text);
  padding: 0.35rem 0.7rem;
  cursor: pointer;
  font-family: inherit;
}

main {
  width: min(1120px, 94%);
  margin: 0 auto;
  padding-top: 3.6rem;
}

.hero { display: grid; gap: 0.7rem; }
.kicker {
  margin: 0;
  color: var(--accent);
