if (!window.MotionPreference) {
  const __mql = window.matchMedia("(prefers-reduced-motion: reduce)");
  const __listeners = new Set();

  const MotionPreference = {
    prefersReducedMotion() {
      return __mql.matches;
    },
    setOverride(value) {
      const reduced = Boolean(value);
      document.documentElement.classList.toggle("reduced-motion", reduced);
      window.dispatchEvent(new CustomEvent("motion-preference", { detail: { reduced } }));
      for (const listener of __listeners) {
        try {
          listener({ reduced, override: reduced, systemReduced: __mql.matches });
        } catch {}
      }
    },
    onChange(listener) {
      __listeners.add(listener);
      try {
        listener({
          reduced: __mql.matches,
          override: null,
          systemReduced: __mql.matches,
        });
      } catch {}
      return () => __listeners.delete(listener);
    },
    getState() {
      return { reduced: __mql.matches, override: null, systemReduced: __mql.matches };
    },
  };

  window.MotionPreference = MotionPreference;
}

function prefersReducedMotion() {
  return window.MotionPreference.prefersReducedMotion();
}

function initDemoShell() {
  // No-op shim in imported standalone snippets.
}

// ── Demo shell ──
initDemoShell({
  title: 'Theme Transition',
  category: 'transitions',
  tech: ['view-transitions-api', 'clip-path'],
});

// ── Check support ──
const supportsVT = typeof document.startViewTransition === 'function';
if (!supportsVT) document.body.classList.add('no-vt');

// ── Refs ──
const root = document.documentElement;
const toggle = document.getElementById('theme-toggle');
const label = document.getElementById('toggle-label');

function getCurrentTheme() {
  return root.getAttribute('data-theme') || 'dark';
}

function updateLabel() {
  const theme = getCurrentTheme();
  label.textContent = theme === 'dark' ? 'Switch to Light' : 'Switch to Dark';
}

// ── Toggle handler ──
toggle.addEventListener('click', (e) => {
  const nextTheme = getCurrentTheme() === 'dark' ? 'light' : 'dark';

  // Set CSS custom properties for click position (used in clip-path animation)
  const rect = toggle.getBoundingClientRect();
  const x = rect.left + rect.width / 2;
  const y = rect.top + rect.height / 2;
  root.style.setProperty('--click-x', `${x}px`);
  root.style.setProperty('--click-y', `${y}px`);

  const applyTheme = () => {
    root.setAttribute('data-theme', nextTheme);
    updateLabel();
  };

  if (supportsVT && !prefersReducedMotion()) {
    const transition = document.startViewTransition(applyTheme);
  } else {
    applyTheme();
  }
});

// ── Init ──
updateLabel();

snippets.css:
/* ═══════════════════════════════════════════════════════
   MAISON DORÉE — Warm Editorial Style
   Palette: Cream, Terracotta, Deep Brown, Gold
   Fonts: Playfair Display (headlines), Cormorant Garamond (body), DM Sans (UI)
   Feel: Luxury French restaurant, editorial magazine
═══════════════════════════════════════════════════════ */

:root {
  --cream:       #f5f0e8;
  --cream-dark:  #ede5d8;
  --parchment:   #e8ddc8;
  --terracotta:  #c1714a;
  --terracotta-d:#a05a38;
  --brown:       #2c1a0e;
  --brown-mid:   #4a2e1a;
  --gold:        #c9a84c;
  --gold-light:  #e6c97a;
  --charcoal:    #1a1209;
  --warm-gray:   #7a6a58;
  --warm-muted:  #9a8878;

  --font-display: 'Playfair Display', Georgia, serif;
  --font-body:    'Cormorant Garamond', Georgia, serif;
  --font-ui:      'DM Sans', system-ui, sans-serif;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

html { scroll-behavior: smooth; }

body {
  font-family: var(--font-body);
  background: var(--cream);
  color: var(--brown);
  overflow-x: hidden;
  font-size: 18px;
  line-height: 1.7;
}

/* ── Navigation ── */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.5rem 3rem;
  transition: all 0.4s ease;
}

.nav.scrolled {
  background: rgba(245, 240, 232, 0.95);
  backdrop-filter: blur(12px);
  padding: 1rem 3rem;
  border-bottom: 1px solid rgba(201, 168, 76, 0.2);
}

.nav-logo {
  font-family: var(--font-display);
  font-size: 1.4rem;
  font-weight: 700;
  color: var(--cream);
  letter-spacing: 0.5px;
  transition: color 0.4s ease;
}

.nav.scrolled .nav-logo { color: var(--brown); }

.nav-links {
  list-style: none;
  display: flex;
  gap: 2.5rem;
}

.nav-links a {
  font-family: var(--font-ui);
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  color: rgba(245, 240, 232, 0.8);
  text-decoration: none;
  transition: color 0.3s ease;
}

.nav.scrolled .nav-links a { color: var(--warm-gray); }

.nav-links a:hover { color: var(--gold); }
.nav.scrolled .nav-links a:hover { color: var(--terracotta); }

.nav-cta {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  color: var(--gold);
  text-decoration: none;
  border: 1px solid var(--gold);
  padding: 0.5rem 1.2rem;
  border-radius: 1px;
  transition: all 0.3s ease;
}

.nav-cta:hover {
  background: var(--gold);
  color: var(--brown);
}

.nav.scrolled .nav-cta { color: var(--terracotta); border-color: var(--terracotta); }
.nav.scrolled .nav-cta:hover { background: var(--terracotta); color: var(--cream); }

/* ── Hero ── */
.hero {
  position: relative;
  height: 100vh;
  min-height: 700px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.hero-image {
  position: absolute;
  inset: 0;
  background:
    linear-gradient(135deg, rgba(193, 113, 74, 0.3) 0%, rgba(44, 26, 14, 0.2) 100%),
    radial-gradient(ellipse at 70% 40%, rgba(201, 168, 76, 0.15) 0%, transparent 60%);
  background-color: var(--brown);

  /* Simulated food photo with CSS gradients */
  background-image:
    radial-gradient(ellipse at 30% 60%, rgba(201, 168, 76, 0.08) 0%, transparent 50%),
    radial-gradient(ellipse at 70% 30%, rgba(193, 113, 74, 0.12) 0%, transparent 50%),
    linear-gradient(160deg, #1a0e06 0%, #2c1a0e 40%, #3a2010 70%, #1a0e06 100%);
}

.hero-overlay {
  position: absolute;
  inset: 0;
  background: linear-gradient(
    to bottom,
    rgba(26, 18, 9, 0.3) 0%,
    rgba(26, 18, 9, 0.1) 40%,
    rgba(26, 18, 9, 0.6) 100%
  );
}

.hero-content {
  position: relative;
  z-index: 2;
  text-align: center;
  max-width: 800px;
  padding: 0 2rem;
}

.hero-pre {
  font-family: var(--font-ui);
  font-size: 0.72rem;
  font-weight: 400;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--gold-light);
  margin-bottom: 2rem;
  opacity: 0.9;
}

.hero-title {
  font-family: var(--font-display);
  font-size: clamp(3.5rem, 8vw, 7rem);
  font-weight: 400;
  line-height: 1.05;
  color: var(--cream);
  margin-bottom: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.1em;
}

.hero-title .line { display: block; overflow: hidden; }

.hero-title .italic {
  font-style: italic;
  color: var(--gold-light);
}

.hero-desc {
  font-family: var(--font-body);
  font-size: 1.15rem;
  font-weight: 300;
  color: rgba(245, 240, 232, 0.75);
  max-width: 500px;
  margin: 0 auto 3rem;
  line-height: 1.8;
}

.hero-actions {
  display: flex;
  gap: 1.5rem;
  justify-content: center;
  flex-wrap: wrap;
}

.btn-gold {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 1rem 2.5rem;
  background: var(--gold);
  color: var(--brown);
  border: none;
  border-radius: 1px;
  transition: all 0.3s ease;
  display: inline-block;
}

.btn-gold:hover {
  background: var(--gold-light);
  transform: translateY(-1px);
  box-shadow: 0 8px 24px rgba(201, 168, 76, 0.3);
}

.btn-ghost-warm {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 1rem 2.5rem;
  color: var(--cream);
  border: 1px solid rgba(245, 240, 232, 0.3);
  border-radius: 1px;
  transition: all 0.3s ease;
  display: inline-block;
}

.btn-ghost-warm:hover {
  border-color: var(--gold);
  color: var(--gold);
}

.btn-outline-warm {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 0.8rem 2rem;
  color: var(--terracotta);
  border: 1px solid var(--terracotta);
  border-radius: 1px;
  transition: all 0.3s ease;
  display: inline-block;
}

.btn-outline-warm:hover {
  background: var(--terracotta);
  color: var(--cream);
}

.hero-scroll {
  position: absolute;
  bottom: 3rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.8rem;
  z-index: 2;
}

.scroll-line {
  width: 1px;
  height: 50px;
  background: linear-gradient(to bottom, var(--gold), transparent);
  animation: scrollPulse 2s ease-in-out infinite;
}

@keyframes scrollPulse {
  0%, 100% { opacity: 0.4; transform: scaleY(1); }
  50% { opacity: 0.9; transform: scaleY(1.2); }
}

.hero-scroll span {
  font-family: var(--font-ui);
  font-size: 0.6rem;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--gold-light);
  opacity: 0.6;
}

/* ── Section Shared ── */
.section-label {
  display: block;
  font-family: var(--font-ui);
  font-size: 0.68rem;
  font-weight: 500;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--terracotta);
  margin-bottom: 1.2rem;
}

/* ── Philosophy ── */
.philosophy-section {
  padding: 8rem 2rem;
  background: var(--brown);
  position: relative;
  overflow: hidden;
}

.philosophy-inner {
  max-width: 900px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 4rem;
  align-items: center;
}

.philosophy-deco {
  position: relative;
}

.deco-circle {
  width: 200px;
  height: 200px;
  border-radius: 50%;
  border: 1px solid rgba(201, 168, 76, 0.2);
  position: relative;
}

.deco-circle::after {
  content: '';
  position: absolute;
  inset: 20px;
  border-radius: 50%;
  border: 1px solid rgba(201, 168, 76, 0.1);
}

.deco-line-h {
  width: 60px;
  height: 1px;
  background: var(--gold);
  margin-top: 2rem;
  opacity: 0.4;
}

.philosophy-text h2 {
  font-family: var(--font-display);
  font-size: clamp(2rem, 4vw, 3.2rem);
  font-weight: 400;
  color: var(--cream);
  line-height: 1.25;
  margin-bottom: 0.5rem;
}

.philosophy-text h2 em {
  font-style: italic;
  color: var(--gold-light);
}

.attr {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  letter-spacing: 1.5px;
  color: var(--warm-muted);
  margin-bottom: 2rem;
}

.body-text {
  font-family: var(--font-body);
  font-size: 1.05rem;
  font-weight: 300;
  color: rgba(232, 221, 200, 0.7);
  line-height: 1.9;
}

/* ── Menu ── */
.menu-section {
  padding: 8rem 2rem;
  background: var(--cream-dark);
}

.menu-header {
  text-align: center;
  max-width: 600px;
  margin: 0 auto 5rem;
}

.menu-header h2 {
  font-family: var(--font-display);
  font-size: clamp(2.2rem, 4vw, 3.5rem);
  font-weight: 400;
  color: var(--brown);
  margin-top: 0.5rem;
}

.menu-grid {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 0;
}

.menu-item {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0;
  border-top: 1px solid rgba(44, 26, 14, 0.12);
}

.menu-item:last-child { border-bottom: 1px solid rgba(44, 26, 14, 0.12); }

.menu-item--reverse { direction: rtl; }
.menu-item--reverse > * { direction: ltr; }

.menu-img {
  height: 360px;
  background-color: var(--brown-mid);
  position: relative;
  overflow: hidden;
}

.menu-img--1 {
  background: linear-gradient(135deg, #3a1e0a 0%, #5a2e12 40%, #8a4a22 70%, #3a1e0a 100%);
}
.menu-img--2 {
  background: linear-gradient(135deg, #1a1005 0%, #3a2010 40%, #6a3820 70%, #1a1005 100%);
}
.menu-img--3 {
  background: linear-gradient(135deg, #2a1808 0%, #4a2810 40%, #7a4830 70%, #2a1808 100%);
}

.menu-img::after {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(ellipse at 50% 50%, rgba(201, 168, 76, 0.08) 0%, transparent 70%);
}

.menu-info {
  padding: 4rem 4rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  background: var(--cream);
}

.menu-course {
  font-family: var(--font-ui);
  font-size: 0.65rem;
  font-weight: 500;
  letter-spacing: 2.5px;
  text-transform: uppercase;
  color: var(--terracotta);
  margin-bottom: 1rem;
}

.menu-info h3 {
  font-family: var(--font-display);
  font-size: 1.8rem;
  font-weight: 400;
  color: var(--brown);
  margin-bottom: 1rem;
  line-height: 1.2;
}

.menu-info p {
  font-family: var(--font-body);
  font-size: 1rem;
  font-weight: 300;
  color: var(--warm-gray);
  line-height: 1.8;
  margin-bottom: 2rem;
}

.menu-price {
  font-family: var(--font-display);
  font-size: 1.4rem;
  font-weight: 400;
  color: var(--gold);
  letter-spacing: 1px;
}

.menu-footer {
  text-align: center;
  margin-top: 4rem;
  padding-top: 3rem;
  border-top: 1px solid rgba(44, 26, 14, 0.12);
}

.menu-footer p {
  font-family: var(--font-body);
  font-size: 1rem;
  font-style: italic;
  color: var(--warm-gray);
  margin-bottom: 1.5rem;
}

/* ── Story ── */
.story-section {
  padding: 8rem 2rem;
  background: var(--parchment);
}

.story-grid {
  max-width: 1100px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 6rem;
  align-items: center;
}

.story-images {
  position: relative;
}

.story-img {
  border-radius: 2px;
}

.story-img--main {
  width: 100%;
  height: 500px;
  background: linear-gradient(160deg, #2c1a0e 0%, #4a2e1a 40%, #6a3820 70%, #2c1a0e 100%);
}

.story-img--accent {
  width: 55%;
  height: 200px;
  position: absolute;
  bottom: -3rem;
  right: -3rem;
  background: linear-gradient(160deg, #c1714a 0%, #a05a38 50%, #c1714a 100%);
  border: 4px solid var(--parchment);
}

.story-year {
  position: absolute;
  top: 2rem;
  left: -2rem;
