  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  color: rgba(255,255,255,0.5);
  opacity: 0;
  transition: opacity 0.2s, background 0.2s;
}

.gallery-card:hover .gc-expand-hint {
  opacity: 1;
  background: rgba(255,255,255,0.1);
}

/* ── Lightbox ── */
.lightbox {
  position: fixed;
  inset: 0;
  z-index: 500;
  background: #060810;
  display: grid;
  grid-template-columns: 1fr 380px;
  /* clip-path starts as an inset rectangle matching the clicked card */
  clip-path: inset(0 0 0 0 round 4px);
}

.lightbox[hidden] {
  display: none;
}

.lb-art {
  height: 100%;
  position: relative;
  overflow: hidden;
}

.lb-content {
  padding: 3rem 2.5rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  border-left: 1px solid var(--border);
  overflow: auto;
}

.lb-meta {
  margin-bottom: 1.5rem;
}

.lb-title {
  font-family: 'Syne', sans-serif;
  font-size: 2.5rem;
  font-weight: 800;
  letter-spacing: -0.02em;
  color: var(--text);
  margin-bottom: 0.4rem;
  line-height: 1.1;
}

.lb-sub {
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
}

.lb-body {
  font-size: 0.95rem;
  color: rgba(232, 234, 240, 0.7);
  line-height: 1.85;
  margin-bottom: 2.5rem;
}

.lb-close {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  background: transparent;
  border: 1px solid var(--border);
  color: var(--muted);
  font-family: 'Inter', sans-serif;
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.06em;
  cursor: pointer;
  border-radius: 2px;
  align-self: flex-start;
  transition: border-color 0.2s, color 0.2s;
}

.lb-close:hover {
  border-color: rgba(255,255,255,0.3);
  color: var(--text);
}

/* Nav buttons */
.lb-nav {
  position: absolute;
  bottom: 2rem;
  left: 2rem;
  display: flex;
  gap: 0.5rem;
}

.lb-prev, .lb-next {
  width: 44px;
  height: 44px;
  border: 1px solid var(--border);
  background: rgba(0,0,0,0.5);
  color: var(--text);
  font-size: 1rem;
  cursor: pointer;
  border-radius: 2px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: border-color 0.2s, background 0.2s;
}

.lb-prev:hover, .lb-next:hover {
  border-color: rgba(255,255,255,0.4);
  background: rgba(255,255,255,0.05);
}

/* ── Responsive ── */
@media (max-width: 900px) {
  .gallery-grid { grid-template-columns: 1fr 1fr; }
  .gallery-card--wide { grid-column: span 2; }
  .lightbox { grid-template-columns: 1fr; }
  .lb-content { border-left: none; border-top: 1px solid var(--border); max-height: 40vh; }
}

@media (max-width: 600px) {
  .gallery-grid { grid-template-columns: 1fr; }
  .gallery-card--wide { grid-column: auto; aspect-ratio: 3/4; }
  .page { padding: 3rem 1.25rem 5rem; }
}

/* ── Reduced motion ── */
html.reduced-motion .gallery-card {
  transition: none;
}
html.reduced-motion .gc-art {
  transition: none;
}

