    description:
      'Woven from raw Belgian flax with naturally frayed edges, this runner brings understated warmth to any table. The oatmeal tone pairs effortlessly with both ceramic and wooden tableware.',
  },
];

/* ═══════════════════════════════════════════════════════════════════
   HERO — SplitText word reveal
   ═══════════════════════════════════════════════════════════════════ */
const headlineEl = document.getElementById('hero-headline');

if (headlineEl) {
  const split = new SplitText(headlineEl, { type: 'words', wordsClass: 'word' });

  if (!reduced) {
    gsap.fromTo(
      split.words,
      { opacity: 0, y: 20 },
      {
        opacity: 1,
        y: 0,
        duration: 0.7,
        ease: 'expo.out',
        stagger: 0.08,
        delay: 0.15,
      }
    );
  } else {
    gsap.set(split.words, { opacity: 1, y: 0 });
  }
}

/* ═══════════════════════════════════════════════════════════════════
   CARD ENTRANCE — ScrollTrigger stagger
   ═══════════════════════════════════════════════════════════════════ */
const cards = gsap.utils.toArray('.product-card');

if (!reduced) {
  gsap.fromTo(
    cards,
    { opacity: 0, y: 30 },
    {
      opacity: 1,
      y: 0,
      duration: 0.6,
      ease: 'expo.out',
      stagger: 0.06,
      scrollTrigger: {
        trigger: '#product-grid',
        start: 'top 85%',
      },
    }
  );
} else {
  gsap.set(cards, { opacity: 1, y: 0 });
}

/* ═══════════════════════════════════════════════════════════════════
   FILTER — GSAP Flip
   ═══════════════════════════════════════════════════════════════════ */
const filterNav = document.getElementById('filter-nav');
let activeFilter = 'all';

function filterProducts(category) {
  if (category === activeFilter) return;
  activeFilter = category;

  // Update pill UI
  filterNav.querySelectorAll('.filter-pill').forEach((pill) => {
    pill.classList.toggle('active', pill.dataset.filter === category);
  });

  const state = Flip.getState('.product-card');

  document.querySelectorAll('.product-card').forEach((card) => {
    const show = category === 'all' || card.dataset.category === category;
    card.style.display = show ? '' : 'none';
  });

  if (!reduced) {
    Flip.from(state, {
      duration: 0.6,
      ease: 'expo.inOut',
      stagger: 0.03,
      absolute: true,
      onComplete: () => ScrollTrigger.refresh(),
    });
  } else {
    ScrollTrigger.refresh();
  }
}

filterNav.addEventListener('click', (e) => {
  const pill = e.target.closest('.filter-pill');
  if (!pill) return;
  filterProducts(pill.dataset.filter);
});

/* ═══════════════════════════════════════════════════════════════════
   PRODUCT DETAIL — View Transitions API
   ═══════════════════════════════════════════════════════════════════ */
const supportsVT = typeof document.startViewTransition === 'function';
const detailOverlay = document.getElementById('detail-overlay');
const detailImage = document.getElementById('detail-image');
const detailTitle = document.getElementById('detail-title');
const detailCategory = document.getElementById('detail-category');
const detailDesc = document.getElementById('detail-desc');
const detailPrice = document.getElementById('detail-price');
const detailAdd = document.getElementById('detail-add');
const detailClose = document.getElementById('detail-close');
const detailBackdrop = document.getElementById('detail-backdrop');
const qtyValue = document.getElementById('qty-value');
const qtyMinus = document.getElementById('qty-minus');
const qtyPlus = document.getElementById('qty-plus');

let activeProductId = null;
let activeCard = null;
let detailQty = 1;

function getProduct(id) {
  return products.find((p) => p.id === id);
}

function populateDetail(product) {
  // Set image gradient + shape class
  detailImage.className = 'detail-image';
  detailImage.classList.add(product.gradient, product.shape);

  detailTitle.textContent = product.name;
  detailCategory.textContent = product.category;
  detailDesc.textContent = product.description;
  detailPrice.textContent = `$${product.price}`;

  detailQty = 1;
  qtyValue.textContent = detailQty;
  activeProductId = product.id;
}

function openDetail(card, product) {
  activeCard = card;
  populateDetail(product);

  const performOpen = () => {
    // Set view-transition-name on the active card only
    card.classList.add('vt-active');
    detailOverlay.classList.add('open');
    detailOverlay.setAttribute('aria-hidden', 'false');
    document.body.style.overflow = 'hidden';
    lenis.stop();
  };

  if (supportsVT && !reduced) {
    document.startViewTransition(() => performOpen());
  } else {
    performOpen();
  }
}

function closeDetail() {
  const performClose = () => {
    detailOverlay.classList.remove('open');
    detailOverlay.setAttribute('aria-hidden', 'true');
    document.body.style.overflow = '';
    lenis.start();

    // Remove vt-active after transition completes
    if (activeCard) {
      activeCard.classList.remove('vt-active');
      activeCard = null;
    }
    activeProductId = null;
  };

  if (supportsVT && !reduced) {
    document.startViewTransition(() => performClose());
  } else {
    performClose();
  }
}

// Open on card click (but not on the Add to Cart button)
document.getElementById('product-grid').addEventListener('click', (e) => {
  const addBtn = e.target.closest('.btn-add');
  if (addBtn) {
    e.stopPropagation();
    addToCart(Number(addBtn.dataset.id));
    return;
  }

  const card = e.target.closest('.product-card');
  if (!card) return;

  const product = getProduct(Number(card.dataset.id));
  if (product) openDetail(card, product);
});

// Close triggers
detailClose.addEventListener('click', closeDetail);
detailBackdrop.addEventListener('click', closeDetail);
document.addEventListener('keydown', (e) => {
  if (e.key === 'Escape') {
    if (detailOverlay.classList.contains('open')) {
      closeDetail();
    } else if (cartSidebar.classList.contains('open')) {
      closeCart();
    }
  }
});

// Quantity controls
qtyMinus.addEventListener('click', () => {
  if (detailQty > 1) {
    detailQty--;
    qtyValue.textContent = detailQty;
  }
});

qtyPlus.addEventListener('click', () => {
  if (detailQty < 99) {
    detailQty++;
    qtyValue.textContent = detailQty;
  }
});

// Add from detail view
detailAdd.addEventListener('click', () => {
  if (activeProductId) {
    for (let i = 0; i < detailQty; i++) {
      addToCart(activeProductId, true);
    }
    closeDetail();
  }
});

/* ═══════════════════════════════════════════════════════════════════
   CART STATE & UI
   ═══════════════════════════════════════════════════════════════════ */
let cart = [];

const cartSidebar = document.getElementById('cart-sidebar');
const cartPanel = document.getElementById('cart-panel');
const cartItems = document.getElementById('cart-items');
const cartEmpty = document.getElementById('cart-empty');
const cartBadge = document.getElementById('cart-badge');
const cartTotalValue = document.getElementById('cart-total-value');
const cartTrigger = document.getElementById('cart-trigger');
const cartClose = document.getElementById('cart-close');
const cartSidebarBackdrop = document.getElementById('cart-sidebar-backdrop');

function addToCart(productId, skipBounce = false) {
  const existing = cart.find((item) => item.id === productId);
  if (existing) {
    existing.qty++;
  } else {
    cart.push({ id: productId, qty: 1 });
  }
  updateCartUI();

  // Badge bounce
  if (!reduced && !skipBounce) {
    gsap.fromTo(
      cartBadge,
      { scale: 1.4 },
      { scale: 1, duration: 0.3, ease: 'back.out(2)' }
    );
  }
}

function removeFromCart(productId) {
  cart = cart.filter((item) => item.id !== productId);
  updateCartUI();
}

function updateCartUI() {
  const totalItems = cart.reduce((sum, item) => sum + item.qty, 0);
  const totalPrice = cart.reduce((sum, item) => {
    const product = getProduct(item.id);
    return sum + (product ? product.price * item.qty : 0);
  }, 0);

  // Badge
  cartBadge.textContent = totalItems;
  cartBadge.classList.toggle('visible', totalItems > 0);

  // Total
  cartTotalValue.textContent = `$${totalPrice}`;

  // Items list
  if (cart.length === 0) {
    cartEmpty.style.display = '';
    // Clear all cart items
    cartItems.querySelectorAll('.cart-item').forEach((el) => el.remove());
    return;
  }

  cartEmpty.style.display = 'none';

  // Rebuild items
  const existingItems = cartItems.querySelectorAll('.cart-item');
  existingItems.forEach((el) => el.remove());

  cart.forEach((item) => {
    const product = getProduct(item.id);
    if (!product) return;

    const row = document.createElement('div');
    row.className = 'cart-item';
    row.innerHTML = `
      <div class="cart-item-swatch ${product.gradient}"></div>
      <div class="cart-item-details">
        <div class="cart-item-name">${product.name}</div>
        <div class="cart-item-meta">Qty: ${item.qty}</div>
      </div>
      <div class="cart-item-price">$${product.price * item.qty}</div>
      <button class="cart-item-remove" data-remove-id="${product.id}" aria-label="Remove ${product.name}">
        <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
          <line x1="18" y1="6" x2="6" y2="18"/>
          <line x1="6" y1="6" x2="18" y2="18"/>
        </svg>
      </button>
    `;
    cartItems.appendChild(row);
  });
}

// Cart item removal (delegated)
cartItems.addEventListener('click', (e) => {
  const removeBtn = e.target.closest('.cart-item-remove');
  if (!removeBtn) return;
  removeFromCart(Number(removeBtn.dataset.removeId));
});

/* ═══════════════════════════════════════════════════════════════════
   CART SIDEBAR OPEN / CLOSE
   ═══════════════════════════════════════════════════════════════════ */
function openCart() {
  cartSidebar.classList.add('open');
  cartSidebar.setAttribute('aria-hidden', 'false');
  document.body.style.overflow = 'hidden';
  lenis.stop();
}

function closeCart() {
  cartSidebar.classList.remove('open');
  cartSidebar.setAttribute('aria-hidden', 'true');
  document.body.style.overflow = '';
  lenis.start();
}

cartTrigger.addEventListener('click', openCart);
cartClose.addEventListener('click', closeCart);
cartSidebarBackdrop.addEventListener('click', closeCart);

snippets.css:
:root {
  --page-bg: #0c0c0c;
  --page-surface: #161616;
  --page-border: #2a2a2a;
  --page-text: #e0e0e0;
  --page-muted: #707070;
  --page-green: #00ff87;
  --page-amber: #ffb800;
  --page-blue: #4a9eff;
  --page-red: #ff4444;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

body {
  background: var(--page-bg);
  color: var(--page-text);
  font-family: 'SF Mono', 'Fira Code', 'JetBrains Mono', 'Cascadia Code', 'Consolas', monospace;
  line-height: 1.6;
  overflow-x: hidden;
}

/* ── Neural canvas background ── */
#neural-canvas {
  position: fixed;
  inset: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;
}

.reduced-motion #neural-canvas { opacity: 0.3; }

/* ── Sections ── */
.section {
  position: relative;
  z-index: 1;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 6rem 2rem;
}

.section-label {
  font-size: 0.75rem;
  color: var(--page-green);
  text-transform: uppercase;
  letter-spacing: 0.15em;
  margin-bottom: 2rem;
  font-weight: 400;
}

/* ── Hero section ── */
.hero-section {
  align-items: center;
  justify-content: center;
  position: relative;
}

/* ── Terminal block ── */
.terminal-block {
  background: rgba(22, 22, 22, 0.92);
  border: 1px solid var(--page-border);
  border-radius: 10px;
  max-width: 600px;
  width: 100%;
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  overflow: hidden;
}

.terminal-header {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1rem;
  background: rgba(30, 30, 30, 0.8);
  border-bottom: 1px solid var(--page-border);
}

.terminal-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
}

.terminal-dot.red { background: var(--page-red); }
.terminal-dot.yellow { background: var(--page-amber); }
.terminal-dot.green { background: var(--page-green); }

.terminal-title {
  font-size: 0.7rem;
  color: var(--page-muted);
  margin-left: 0.5rem;
}

.terminal-body {
  padding: 1.25rem;
  font-size: clamp(0.85rem, 2vw, 1rem);
  line-height: 1.8;
}

.terminal-line {
  min-height: 1.6em;
  white-space: pre-wrap;
}

.terminal-blank {
  height: 0.8em;
}

.prompt {
  color: var(--page-green);
  font-weight: 700;
}

.cmd-text, .name-text, .role-text, .tagline1-text, .tagline2-text,
.contact-cmd, .contact-line {
  color: var(--page-text);
}

.name-text {
  font-size: 1.4em;
  font-weight: 700;
  color: var(--page-green);
}

.role-text {
  color: var(--page-muted);
}

.cursor {
  display: inline-block;
  color: var(--page-green);
  animation: blink 1s step-end infinite;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0; }
}

.reduced-motion .cursor { animation: none; opacity: 1; }

/* ── Scroll hint ── */
.scroll-hint {
  position: absolute;
  bottom: 3rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  color: var(--page-muted);
  font-size: 0.7rem;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  opacity: 0;
}

.scroll-dot {
  animation: scroll-bounce 2s ease-in-out infinite;
}

@keyframes scroll-bounce {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(8px); opacity: 0.3; }
}

.reduced-motion .scroll-dot { animation: none; }

/* ── About section ── */
.about-section {
  padding: 8rem 2rem;
}

.about-grid {
  max-width: 1000px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1.2fr 0.8fr;
  gap: 4rem;
  align-items: start;
}

.bio-text {
  font-size: clamp(0.9rem, 1.8vw, 1.05rem);
  color: var(--page-muted);
  line-height: 1.8;
  font-family: 'Inter', 'SF Pro Display', system-ui, sans-serif;
}

.about-stats {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.stat-card {
  padding: 1.25rem 1.5rem;
  background: var(--page-surface);
  border: 1px solid var(--page-border);
  border-left: 3px solid var(--page-green);
  border-radius: 6px;
}

.stat-number {
  display: block;
  font-size: 2rem;
  font-weight: 700;
  color: var(--page-green);
  line-height: 1.2;
}

.stat-label {
  display: block;
  font-size: 0.75rem;
  color: var(--page-muted);
  margin-top: 0.25rem;
  text-transform: uppercase;
  letter-spacing: 0.08em;
}

/* ── Projects section ── */
.projects-section {
  padding: 8rem 2rem;
  min-height: auto;
}

.projects-section .section-label {
  max-width: 1000px;
  margin-left: auto;
  margin-right: auto;
  width: 100%;
}

.project-card {
  max-width: 1000px;
  margin: 0 auto 3rem;
  background: var(--page-surface);
  border: 1px solid var(--page-border);
  border-radius: 10px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  overflow: hidden;
  opacity: 0;
  transform: translateY(60px);
}

.reduced-motion .project-card {
  opacity: 1;
  transform: none;
}

.project-content {
  padding: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.project-title {
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--page-text);
  margin-bottom: 0.75rem;
}

.project-card[data-project="neurovox"] .project-title { color: var(--page-green); }
.project-card[data-project="deepsight"] .project-title { color: var(--page-amber); }
.project-card[data-project="synthmind"] .project-title { color: var(--page-blue); }

.project-desc {
  font-size: 0.85rem;
  color: var(--page-muted);
  line-height: 1.7;
