/* ═══════════════════════════════════════════════════════
   IRONFORM — Brutalist Bold Style
   Palette: Pure Black, Neon Yellow, White, Concrete Gray
   Fonts: Barlow Condensed (display), Barlow (body)
   Feel: Raw, aggressive, no-nonsense fitness brand
═══════════════════════════════════════════════════════ */

:root {
  --black:      #0a0a0a;
  --black-mid:  #151515;
  --concrete:   #1e1e1e;
  --gray:       #2a2a2a;
  --gray-mid:   #444;
  --gray-light: #888;
  --white:      #f5f5f5;
  --yellow:     #f0e000;
  --yellow-d:   #c8bb00;
  --yellow-dim: rgba(240, 224, 0, 0.12);

  --font-display: 'Barlow Condensed', 'Arial Narrow', sans-serif;
  --font-body:    'Barlow', system-ui, sans-serif;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

body {
  font-family: var(--font-body);
  background: var(--black);
  color: var(--white);
  overflow-x: hidden;
}

/* ── Nav ── */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.2rem 3rem;
  background: var(--black);
  border-bottom: 3px solid var(--yellow);
}

.nav-logo {
  font-family: var(--font-display);
  font-size: 1.8rem;
  font-weight: 900;
  letter-spacing: -1px;
  color: var(--white);
}

.nav-logo span { color: var(--yellow); }

.nav-links {
  list-style: none;
  display: flex;
  gap: 3rem;
}

.nav-links a {
  font-family: var(--font-display);
  font-size: 1rem;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: var(--gray-light);
  text-decoration: none;
  transition: color 0.15s ease;
}

.nav-links a:hover { color: var(--yellow); }

.nav-cta {
  font-family: var(--font-display);
  font-size: 0.95rem;
  font-weight: 900;
  letter-spacing: 2px;
  color: var(--black);
  background: var(--yellow);
  text-decoration: none;
  padding: 0.6rem 1.5rem;
  border: none;
  transition: background 0.15s ease, transform 0.15s ease;
  display: inline-block;
}

.nav-cta:hover { background: var(--yellow-d); transform: translateY(-1px); }

/* ── Hero ── */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  padding: 8rem 4rem 6rem;
  overflow: hidden;
  background: var(--black);
}

.hero-bg { position: absolute; inset: 0; pointer-events: none; }

.hero-stripe {
  position: absolute;
  background: var(--yellow);
  opacity: 0.04;
}

.hero-stripe--1 {
  width: 3px;
  top: 0; bottom: 0;
  right: 28%;
}

.hero-stripe--2 {
  height: 3px;
  left: 0; right: 0;
  bottom: 30%;
  opacity: 0.03;
}

.hero-content {
  position: relative;
  z-index: 2;
  max-width: 900px;
}

.hero-tag {
  font-family: var(--font-display);
  font-size: 0.8rem;
  font-weight: 700;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--gray-light);
  margin-bottom: 2rem;
  border-left: 3px solid var(--yellow);
  padding-left: 1rem;
}

.hero-title {
  display: flex;
  flex-direction: column;
  margin-bottom: 2.5rem;
  line-height: 0.92;
}

.hero-line {
  font-family: var(--font-display);
  font-size: clamp(5rem, 14vw, 12rem);
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: -3px;
  color: var(--white);
  display: block;
}

.hero-line--yellow { color: var(--yellow); }

.hero-line--outline {
  color: transparent;
  -webkit-text-stroke: 3px var(--white);
}

.hero-sub {
  font-family: var(--font-body);
  font-size: 1.1rem;
  font-weight: 400;
  color: var(--gray-light);
  max-width: 500px;
  margin-bottom: 3rem;
  line-height: 1.7;
}

.hero-ctas {
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
}

.btn-yellow {
  font-family: var(--font-display);
  font-size: 1rem;
  font-weight: 900;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 1.1rem 2.5rem;
  background: var(--yellow);
  color: var(--black);
  border: none;
  display: inline-block;
  transition: all 0.15s ease;
  cursor: pointer;
}

.btn-yellow:hover { background: var(--yellow-d); transform: translateY(-2px); }

.btn-border-white {
  font-family: var(--font-display);
  font-size: 1rem;
  font-weight: 900;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 1.1rem 2.5rem;
  background: transparent;
  color: var(--white);
  border: 2px solid var(--gray-mid);
  display: inline-block;
  transition: all 0.15s ease;
}

.btn-border-white:hover { border-color: var(--yellow); color: var(--yellow); }

/* Ticker */
.hero-ticker {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: var(--yellow);
  overflow: hidden;
  padding: 0.7rem 0;
}

.ticker-track {
  display: flex;
  gap: 2rem;
  white-space: nowrap;
  animation: ticker 25s linear infinite;
  width: max-content;
}

.ticker-track span {
  font-family: var(--font-display);
  font-size: 0.85rem;
  font-weight: 900;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--black);
}

@keyframes ticker { from { transform: translateX(0); } to { transform: translateX(-50%); } }

/* ── Stats Bar ── */
.stats-bar {
  display: flex;
  align-items: stretch;
  background: var(--concrete);
  border-bottom: 3px solid var(--yellow);
}

.stat-item {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 3.5rem 2rem;
  gap: 0.5rem;
}

.stat-divider {
  width: 1px;
  background: var(--gray);
  align-self: stretch;
}

.stat-big {
  font-family: var(--font-display);
  font-size: clamp(3rem, 5vw, 5rem);
  font-weight: 900;
  color: var(--yellow);
  line-height: 1;
  letter-spacing: -2px;
  font-variant-numeric: tabular-nums;
}

.stat-sub {
  font-family: var(--font-display);
  font-size: 0.85rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--gray-light);
}

/* ── Programs ── */
.programs-section {
  padding: 7rem 4rem;
  background: var(--black-mid);
}

.programs-header {
  margin-bottom: 4rem;
  max-width: 600px;
}

.section-tag {
  display: block;
  font-family: var(--font-display);
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--yellow);
  margin-bottom: 1rem;
  border-left: 3px solid var(--yellow);
  padding-left: 1rem;
}

.programs-header h2 {
  font-family: var(--font-display);
  font-size: clamp(3.5rem, 7vw, 6rem);
  font-weight: 900;
  line-height: 0.95;
  letter-spacing: -2px;
  text-transform: uppercase;
}

.programs-header h2 em {
  font-style: italic;
  color: var(--yellow);
}

.programs-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 0;
  border: 2px solid var(--gray);
}

.program-card {
  padding: 3rem 2.5rem;
  border-right: 2px solid var(--gray);
  display: flex;
  flex-direction: column;
  gap: 1.2rem;
  transition: transform 0.2s ease;
}

.program-card:last-child { border-right: none; }

.program-card--dark { background: var(--concrete); }
.program-card--yellow { background: var(--yellow); }

.program-card--yellow h3,
.program-card--yellow p,
.program-card--yellow .program-meta span { color: var(--black) !important; }

.program-num {
  font-family: var(--font-display);
  font-size: 4rem;
  font-weight: 900;
  line-height: 1;
  color: rgba(255,255,255,0.08);
  letter-spacing: -2px;
}

.program-card--yellow .program-num { color: rgba(0,0,0,0.1); }

.program-card h3 {
  font-family: var(--font-display);
  font-size: 2rem;
  font-weight: 900;
  letter-spacing: -0.5px;
  text-transform: uppercase;
  color: var(--white);
}

.program-card p {
  font-family: var(--font-body);
  font-size: 0.95rem;
  color: var(--gray-light);
  line-height: 1.7;
  flex: 1;
}

.program-meta {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.program-meta span {
  font-family: var(--font-display);
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: var(--gray-light);
  padding: 0.3rem 0.6rem;
  border: 1px solid var(--gray);
}

.program-card--yellow .program-meta span { border-color: rgba(0,0,0,0.2); }

.btn-yellow-sm {
  font-family: var(--font-display);
  font-size: 0.85rem;
  font-weight: 900;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  background: var(--yellow);
  color: var(--black);
  display: inline-block;
  transition: all 0.15s ease;
  align-self: flex-start;
}

.btn-yellow-sm:hover { background: var(--yellow-d); }

.btn-black-sm {
  font-family: var(--font-display);
  font-size: 0.85rem;
  font-weight: 900;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 0.8rem 1.5rem;
  background: var(--black);
  color: var(--yellow);
  border: 2px solid var(--black);
  display: inline-block;
  transition: all 0.15s ease;
  align-self: flex-start;
}

.btn-black-sm:hover { background: var(--black-mid); }

/* ── Transform ── */
.transform-section {
  padding: 7rem 4rem;
  background: var(--black);
  border-top: 3px solid var(--yellow);
  border-bottom: 3px solid var(--yellow);
}

.transform-inner {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
