/* ── Demo 47: Music Platform — Neon Cyberpunk ── */
/* Palette: Black / Neon Magenta / Cyan / Lime */
/* Fonts: Space Mono (display/code) + Space Grotesk (body) */

:root {
  --black: #080810;
  --dark: #0d0d1a;
  --panel: #111120;
  --border: #1e1e38;
  --text: #e8e8f8;
  --muted: #6b6b90;
  --faint: #2a2a48;

  --magenta: #ff2d78;
  --cyan: #00e5ff;
  --lime: #a8ff3e;
  --purple: #9b4dff;
  --gold: #ffcc00;

  --neon-glow-m: 0 0 8px rgba(255, 45, 120, 0.6), 0 0 25px rgba(255, 45, 120, 0.25);
  --neon-glow-c: 0 0 8px rgba(0, 229, 255, 0.6), 0 0 25px rgba(0, 229, 255, 0.25);
}

*, *::before, *::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Space Grotesk', -apple-system, sans-serif;
  background: var(--black);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* ── Scan lines overlay ── */
.scanlines {
  position: fixed;
  inset: 0;
  z-index: 200;
  pointer-events: none;
  background: repeating-linear-gradient(
    0deg,
    transparent,
    transparent 2px,
    rgba(0, 0, 0, 0.06) 2px,
    rgba(0, 0, 0, 0.06) 4px
  );
}

/* ── Navigation ── */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.25rem 2.5rem;
  background: rgba(8, 8, 16, 0.85);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid var(--border);
}

.nav-brand {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.brand-mark {
  font-size: 1.2rem;
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
}

.brand-name {
  font-family: 'Space Mono', monospace;
  font-size: 1.1rem;
  font-weight: 700;
  letter-spacing: 0.1em;
  color: var(--text);
}

.nav-links {
  display: flex;
  gap: 2.5rem;
  list-style: none;
}

.nav-links a {
  text-decoration: none;
  font-family: 'Space Mono', monospace;
  font-size: 0.72rem;
  font-weight: 400;
  letter-spacing: 0.12em;
  color: var(--muted);
  text-transform: uppercase;
  transition: color 0.2s;
}

.nav-links a:hover {
  color: var(--cyan);
  text-shadow: var(--neon-glow-c);
}

.nav-right {
  display: flex;
  align-items: center;
  gap: 1.5rem;
}

.nav-live {
  display: flex;
  align-items: center;
  gap: 0.4rem;
  font-family: 'Space Mono', monospace;
  font-size: 0.68rem;
  letter-spacing: 0.15em;
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
}

.live-dot {
  width: 6px;
  height: 6px;
  background: var(--magenta);
  border-radius: 50%;
  animation: pulse-dot 1.2s ease-in-out infinite;
  box-shadow: var(--neon-glow-m);
}

@keyframes pulse-dot {
  0%, 100% { opacity: 1; transform: scale(1); }
  50% { opacity: 0.4; transform: scale(0.7); }
}

/* ── Buttons ── */
.btn-neon {
  display: inline-block;
  padding: 0.7rem 1.5rem;
  background: transparent;
  color: var(--magenta);
  border: 1px solid var(--magenta);
  font-family: 'Space Mono', monospace;
  font-size: 0.72rem;
  font-weight: 700;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  text-decoration: none;
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: inset 0 0 0 0 var(--magenta);
}

.btn-neon:hover {
  background: var(--magenta);
  color: var(--black);
  box-shadow: var(--neon-glow-m);
}

.btn-neon--lg {
  padding: 1rem 2.5rem;
  font-size: 0.82rem;
}

.btn-ghost {
  display: inline-flex;
  align-items: center;
  gap: 0.6rem;
  padding: 0.9rem 1.8rem;
  background: transparent;
  color: var(--text);
  border: 1px solid var(--faint);
  font-family: 'Space Grotesk', sans-serif;
  font-size: 0.85rem;
  font-weight: 500;
  cursor: pointer;
  transition: border-color 0.2s, color 0.2s;
}

.btn-ghost:hover {
  border-color: var(--cyan);
  color: var(--cyan);
}

.play-icon {
  font-size: 0.7rem;
}

.btn-outline {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  background: transparent;
  color: var(--text);
  border: 1px solid var(--border);
  font-family: 'Space Mono', monospace;
  font-size: 0.72rem;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  text-decoration: none;
  transition: border-color 0.2s, color 0.2s;
}

.btn-outline:hover {
  border-color: var(--cyan);
  color: var(--cyan);
}

/* ── Hero ── */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 7rem 2.5rem 5rem;
  overflow: hidden;
}

#waveform-canvas {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
}

.hero-content {
  position: relative;
  z-index: 1;
  max-width: 720px;
}

.hero-tag {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 2rem;
  font-family: 'Space Mono', monospace;
  font-size: 0.75rem;
  letter-spacing: 0.1em;
  color: var(--muted);
}

.tag-prefix {
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
  font-size: 0.9rem;
}

.hero-h1 {
  font-family: 'Space Mono', monospace;
  font-size: clamp(3.5rem, 9vw, 8rem);
  font-weight: 700;
  line-height: 0.95;
  letter-spacing: -0.03em;
  color: var(--text);
  margin-bottom: 1.5rem;
  display: flex;
  flex-direction: column;
}

.h1-line--neon {
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
}

.hero-sub {
  font-size: 1rem;
  color: var(--muted);
  line-height: 1.8;
  max-width: 520px;
  margin-bottom: 2.5rem;
}

.hero-actions {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 3rem;
}

/* EQ bars */
.eq-bars {
  display: flex;
  align-items: flex-end;
  gap: 3px;
  height: 32px;
}

.eq-bars span {
  width: 4px;
  background: var(--magenta);
  border-radius: 1px;
  animation: eq-bounce 0.8s ease-in-out infinite alternate;
  box-shadow: 0 0 4px rgba(255, 45, 120, 0.5);
}

.eq-bars span:nth-child(1)  { height: 60%; animation-delay: 0.0s; }
.eq-bars span:nth-child(2)  { height: 90%; animation-delay: 0.1s; }
.eq-bars span:nth-child(3)  { height: 40%; animation-delay: 0.2s; }
.eq-bars span:nth-child(4)  { height: 100%;animation-delay: 0.15s; }
.eq-bars span:nth-child(5)  { height: 70%; animation-delay: 0.3s; }
.eq-bars span:nth-child(6)  { height: 55%; animation-delay: 0.25s; }
.eq-bars span:nth-child(7)  { height: 85%; animation-delay: 0.05s; }
.eq-bars span:nth-child(8)  { height: 45%; animation-delay: 0.35s; }
.eq-bars span:nth-child(9)  { height: 75%; animation-delay: 0.1s; }
.eq-bars span:nth-child(10) { height: 95%; animation-delay: 0.2s; }
.eq-bars span:nth-child(11) { height: 50%; animation-delay: 0.4s; }
.eq-bars span:nth-child(12) { height: 80%; animation-delay: 0.15s; }
.eq-bars span:nth-child(13) { height: 65%; animation-delay: 0.05s; }
.eq-bars span:nth-child(14) { height: 90%; animation-delay: 0.3s; }
.eq-bars span:nth-child(15) { height: 40%; animation-delay: 0.0s; }

@keyframes eq-bounce {
  from { transform: scaleY(0.35); }
  to   { transform: scaleY(1); }
}

/* Now playing card */
.now-playing {
  position: absolute;
  right: 3rem;
  bottom: 4rem;
  width: 280px;
  padding: 1.25rem;
  background: rgba(13, 13, 26, 0.9);
  border: 1px solid var(--magenta);
  box-shadow: var(--neon-glow-m);
  z-index: 2;
}

.np-label {
  font-family: 'Space Mono', monospace;
  font-size: 0.62rem;
  letter-spacing: 0.18em;
  color: var(--magenta);
  margin-bottom: 0.5rem;
}

.np-title {
  font-family: 'Space Mono', monospace;
  font-size: 0.95rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.2rem;
}

.np-artist {
  font-size: 0.78rem;
  color: var(--muted);
  margin-bottom: 1rem;
}

.np-bar {
  height: 2px;
  background: var(--faint);
  margin-bottom: 0.5rem;
  overflow: hidden;
}

.np-progress {
  height: 100%;
  width: 50%;
  background: var(--magenta);
  box-shadow: 0 0 6px var(--magenta);
}

.np-time {
  display: flex;
  justify-content: space-between;
  font-family: 'Space Mono', monospace;
  font-size: 0.68rem;
  color: var(--muted);
}

/* ── Section tag ── */
.section-tag {
  display: block;
  font-family: 'Space Mono', monospace;
  font-size: 0.68rem;
  letter-spacing: 0.15em;
  color: var(--cyan);
  text-shadow: var(--neon-glow-c);
  margin-bottom: 1rem;
}

.neon-text {
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
}

/* ── Shows Section ── */
.shows-section {
  padding: 5rem 2.5rem;
  border-top: 1px solid var(--border);
}

.shows-header {
  margin-bottom: 2.5rem;
}

.shows-header h2 {
  font-family: 'Space Mono', monospace;
  font-size: clamp(2rem, 5vw, 4rem);
  font-weight: 700;
  letter-spacing: -0.02em;
  color: var(--text);
  line-height: 1;
}

.shows-grid {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: auto auto;
  gap: 1px;
  background: var(--border);
  border: 1px solid var(--border);
}

.show-card--featured {
  grid-row: 1 / 3;
}

.show-card {
  background: var(--dark);
  overflow: hidden;
  transition: background 0.3s;
  cursor: pointer;
}

.show-card:hover {
  background: var(--panel);
}

.sc-art {
  height: 200px;
  position: relative;
}

.show-card--featured .sc-art {
  height: 300px;
}

.sc-art--1 {
  background:
    radial-gradient(ellipse at 30% 50%, rgba(255, 45, 120, 0.4) 0%, transparent 60%),
    radial-gradient(ellipse at 70% 30%, rgba(155, 77, 255, 0.35) 0%, transparent 50%),
    var(--dark);
}

.sc-art--2 {
  background:
    radial-gradient(ellipse at 50% 70%, rgba(0, 229, 255, 0.3) 0%, transparent 60%),
    radial-gradient(ellipse at 20% 20%, rgba(168, 255, 62, 0.2) 0%, transparent 50%),
    var(--dark);
}

.sc-art--3 {
  background:
    radial-gradient(ellipse at 80% 50%, rgba(155, 77, 255, 0.4) 0%, transparent 60%),
    radial-gradient(ellipse at 30% 80%, rgba(0, 229, 255, 0.2) 0%, transparent 50%),
    var(--dark);
}

.sc-art--4 {
  background:
    radial-gradient(ellipse at 50% 30%, rgba(255, 204, 0, 0.25) 0%, transparent 55%),
    radial-gradient(ellipse at 70% 70%, rgba(255, 45, 120, 0.25) 0%, transparent 50%),
    var(--dark);
}

.sc-info {
  padding: 1.5rem;
  border-top: 1px solid var(--border);
}

.sc-info--sm {
  padding: 1.2rem;
}

.sc-genre {
  font-family: 'Space Mono', monospace;
  font-size: 0.62rem;
  letter-spacing: 0.18em;
  color: var(--cyan);
  display: block;
  margin-bottom: 0.4rem;
}

.sc-info h3 {
  font-family: 'Space Mono', monospace;
  font-size: 1.2rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.6rem;
}

.sc-info--sm h3 {
  font-size: 0.95rem;
}

.sc-info p {
  font-size: 0.85rem;
  color: var(--muted);
  line-height: 1.65;
