.sc-stats span { display: flex; gap: 0.4rem; }
.sc-stats em {
  font-style: normal;
  color: var(--muted);
  font-size: 0.65rem;
  align-self: flex-end;
}

/* ── Charts Section ── */
.charts-section {
  padding: 5rem 2.5rem;
  background: var(--dark);
  border-top: 1px solid var(--border);
}

.charts-inner {
  max-width: 900px;
}

.charts-header {
  margin-bottom: 3rem;
}

.charts-header h2 {
