}

.ci-rank {
  font-family: 'Space Mono', monospace;
  font-size: 1.2rem;
  font-weight: 700;
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
}

.ci-info {
  display: flex;
  flex-direction: column;
  gap: 0.2rem;
}

.ci-info strong {
  font-family: 'Space Mono', monospace;
  font-size: 0.85rem;
  font-weight: 700;
  color: var(--text);
  letter-spacing: 0.02em;
}

.ci-info span {
  font-size: 0.75rem;
  color: var(--muted);
}

.ci-bar-wrap {
  height: 2px;
  background: var(--faint);
  overflow: hidden;
}

.ci-bar {
  height: 100%;
  width: var(--w, 0%);
  background: linear-gradient(90deg, var(--magenta), var(--purple));
  transform-origin: left;
  transform: scaleX(0);
  box-shadow: 0 0 4px var(--magenta);
}

.ci-plays {
  font-family: 'Space Mono', monospace;
  font-size: 0.72rem;
  color: var(--muted);
  text-align: right;
}

.ci-delta {
  font-family: 'Space Mono', monospace;
  font-size: 0.65rem;
  font-weight: 700;
  text-align: center;
}

.ci-delta.up   { color: var(--lime); }
.ci-delta.down { color: var(--magenta); }
.ci-delta.new  { color: var(--gold); }

/* ── Artists Section ── */
.artists-section {
  padding: 5rem 2.5rem;
  border-top: 1px solid var(--border);
}

.artists-header {
  margin-bottom: 3rem;
}

.artists-header h2 {
  font-family: 'Space Mono', monospace;
  font-size: clamp(2rem, 5vw, 3.5rem);
  font-weight: 700;
  line-height: 1.1;
  color: var(--text);
}

/* Marquee */
.artists-marquee-wrap {
  overflow: hidden;
  border-top: 1px solid var(--border);
  border-bottom: 1px solid var(--border);
  padding: 1rem 0;
  margin-bottom: 4rem;
}

.marquee-track {
  display: flex;
  gap: 1rem;
  width: max-content;
  animation: marquee-scroll 28s linear infinite;
}

@keyframes marquee-scroll {
  from { transform: translateX(0); }
  to   { transform: translateX(-50%); }
}

.artist-chip {
  flex-shrink: 0;
  padding: 0.5rem 1.25rem;
  border: 1px solid var(--border);
  font-family: 'Space Mono', monospace;
  font-size: 0.72rem;
  letter-spacing: 0.08em;
  color: var(--muted);
  white-space: nowrap;
  transition: border-color 0.2s, color 0.2s;
}

.chip--accent {
  border-color: rgba(255, 45, 120, 0.4);
  color: var(--magenta);
}

/* Platform stats */
.platform-stats {
  display: flex;
  align-items: center;
  gap: 3rem;
  padding: 2.5rem 2rem;
  border: 1px solid var(--border);
  background: var(--panel);
}

.ps-item {
  display: flex;
  flex-direction: column;
  gap: 0.3rem;
}

.ps-num {
  font-family: 'Space Mono', monospace;
  font-size: 2.2rem;
  font-weight: 700;
  color: var(--cyan);
  text-shadow: var(--neon-glow-c);
  line-height: 1;
}

.ps-label {
  font-size: 0.75rem;
  color: var(--muted);
  letter-spacing: 0.05em;
}

.ps-div {
  width: 1px;
  height: 50px;
  background: var(--border);
}

/* ── Studio / CTA ── */
.studio-section {
  padding: 5rem 2.5rem;
  background: var(--dark);
  border-top: 1px solid var(--border);
}

.studio-inner {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}

.studio-left h2 {
  font-family: 'Space Mono', monospace;
  font-size: clamp(2rem, 4vw, 3.2rem);
  font-weight: 700;
  line-height: 1.1;
  color: var(--text);
  margin-bottom: 1.2rem;
}

.studio-left p {
  font-size: 0.95rem;
  color: var(--muted);
  line-height: 1.8;
  max-width: 400px;
  margin-bottom: 2rem;
}

.studio-links {
  display: flex;
  gap: 1rem;
  align-items: center;
}

/* Terminal */
.studio-terminal {
  background: #0a0a14;
  border: 1px solid var(--border);
  border-top: 1px solid var(--cyan);
  box-shadow: var(--neon-glow-c);
  font-family: 'Space Mono', monospace;
  font-size: 0.82rem;
  overflow: hidden;
}

.terminal-bar {
  display: flex;
  align-items: center;
  gap: 0.4rem;
  padding: 0.6rem 1rem;
  background: var(--panel);
  border-bottom: 1px solid var(--border);
}

.tb-dot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
}
.tb-dot--red    { background: #ff5f56; }
.tb-dot--yellow { background: #ffbd2e; }
.tb-dot--green  { background: #27c93f; }

.tb-title {
  margin-left: 0.5rem;
  font-size: 0.7rem;
  color: var(--muted);
}

.terminal-body {
  padding: 1.2rem;
  display: flex;
  flex-direction: column;
  gap: 0.3rem;
  min-height: 180px;
}

.terminal-body p {
  line-height: 1.5;
}

.t-prompt {
  color: var(--lime);
  margin-right: 0.5rem;
}

.t-output {
  color: var(--muted);
  padding-left: 1.2rem;
}

.t-success { color: var(--lime); }

.t-val {
  color: var(--text);
}

.t-neon {
  color: var(--magenta);
  text-shadow: var(--neon-glow-m);
}

.t-cursor {
  color: var(--cyan);
  animation: cursor-blink 1s step-end infinite;
}

@keyframes cursor-blink {
  0%, 100% { opacity: 1; }
  50%       { opacity: 0; }
}

/* ── Footer ── */
.footer {
  padding: 1.5rem 2.5rem;
  background: var(--black);
  border-top: 1px solid var(--border);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.footer-brand {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-family: 'Space Mono', monospace;
  font-size: 0.78rem;
  color: var(--muted);
}

.footer-brand .brand-mark {
  font-size: 0.9rem;
}

.footer p {
  font-family: 'Space Mono', monospace;
  font-size: 0.65rem;
  color: var(--muted);
  letter-spacing: 0.05em;
}

/* ── Responsive ── */
@media (max-width: 1024px) {
  .shows-grid { grid-template-columns: 1fr 1fr; }
  .show-card--featured { grid-column: 1 / 3; grid-row: auto; }
  .studio-inner { grid-template-columns: 1fr; }
  .chart-item { grid-template-columns: 40px 1fr 100px 60px 40px; }
}

@media (max-width: 768px) {
  .nav-links { display: none; }
  .now-playing { display: none; }
  .shows-grid { grid-template-columns: 1fr; }
  .show-card--featured { grid-column: auto; }
  .chart-item { grid-template-columns: 40px 1fr 50px 40px; }
  .ci-bar-wrap { display: none; }
  .platform-stats { flex-wrap: wrap; gap: 1.5rem; }
}

/* ── Reduced Motion ── */
html.reduced-motion .eq-bars span,
html.reduced-motion .live-dot,
html.reduced-motion .t-cursor,
html.reduced-motion .marquee-track {
  animation: none !important;
}

html.reduced-motion .eq-bars span {
  height: 60% !important;
  transform: none !important;
}

html.reduced-motion * {
  transition-duration: 0.01ms !important;
}

