.dot {
  display: block;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #38bdf8;
  animation: dotBounce 1.2s ease-in-out infinite;
}

.dot:nth-child(2) { animation-delay: 0.2s; }
.dot:nth-child(3) { animation-delay: 0.4s; }

@keyframes dotBounce {
  0%, 60%, 100% {
    transform: translateY(0) scale(1);
    opacity: 0.6;
  }
  30% {
    transform: translateY(-8px) scale(1.1);
    opacity: 1;
  }
}

